    GENERIC_HANG_UP = "[ZAVĚSIT]",
    ASK_CEO_ABOUT_REVIEWERS_NO_HELP_2 = "Neslyšel jste mě? Říkal jsem, že vám nepomůžu.",
    ASK_CEO_ABOUT_REVIEWERS_NO_HELP_3 = "Přestaňte ztrácet můj čas.",
    ASK_CEO_ABOUT_REVIEWERS_NO_HELP_SLANDER = "Víte...možná bych vám pomohl, ale vzhledem k tomu, že se vaše společnost pokusila zničit reputaci té mé...Na pomoc ode mě můžete zapomenout.",
    ASK_CEO_ABOUT_REVIEWERS_NO_HELP_STEALING = "Proč se nezeptáte jednoho ze zaměstnanců, které jste mi ukradl?",
    ASK_CEO_ABOUT_REVIEWERS_NO_HELP_1 = "Vy a já nemáme zrovna nejlepší vztah. Nepomůžu vám.",
