    ASK_CEO_ABOUT_REVIEWERS_RETURN = "[ZPĚT]",
    ASK_CEO_ABOUT_REVIEWERS_RETURN_DIALOGUE = "Potřebujete ještě něco?",
    RIVAL_COMPANY_THREATEN_1 = "Zdravím, příteli.",
    RIVAL_COMPANY_THREATEN_2 = "Jsem ředitel COMPANY a nevolal bych, kdyby jste se starali jenom o sebe. Vaše společnost si prostě musela zkusit zahrávat se mnou a mými zaměstnanci, co?",
    RIVAL_COMPANY_THREATEN_3 = "Co chci? Nic, jenom jsem vás chtěl upozornit, že jste si v tomto průmyslu udělali nového nepřítele. Nechám vás ochutnat vaši vlastní medicínu.",
    RIVAL_COMPANY_THREATEN_4_A = "Něco udělat? To má být vtip? Obzvláště po tom, co jste mi přebral zaměstnance EMPLOYEE? Buď máte opravdu špatný smysl pro humor, nebo nemáte respekt pro jiné lidi.",
    RIVAL_COMPANY_THREATEN_4_B = "Něco udělat? To má být nějakej vtip? Obzvláště po tom, co jste mi ukradl několik zaměstnanců? Buď máte opravdu špatný smysl pro humor, nebo nemáte respekt pro jiné lidi.",
    RIVAL_COMPANY_THREATEN_4_C = "Něco udělat? To má být vtip? Obzvláště po tom, co jste mi ukradl jednoho z mých zaměstnanců? Buď máte opravdu špatný smysl pro humor, nebo nemáte respekt pro jiné lidi.",
    RIVAL_COMPANY_THREATEN_4_D = "Haha, velice vtipné. Nejdříve přesvědčíte jednoho z mých zaměstnanců, aby odešel do vaší společnosti, a potom se to pokoušíte vynahradit? Vy jste se snad zbláznil.",
    RIVAL_COMPANY_THREATEN_4_SLANDER = "Navíc jste měl takovou drzost, abyste začal pomlouvačnou kampaň na účet mé společnosti. Už pro vás v žádném případě nic nikdy neudělám.",
    RIVAL_COMPANY_THREATEN_5_SLANDER = "Jste blázen, nebo nemáte žádné sebeuvědomění?! Nejdřív jste se pokusil poškodit reputaci mě a mou společnost s nějakou pomluvačnou kampaní a teď se to snažíte vynahradit?! Kdybychom teď stáli proti sobě, klidně bych vám jednu vrazil.",
    RIVAL_COMPANY_THREAT_RESPONSE_CONTINUE_1 = "Dobrý den, potřebujete něco?",
    RIVAL_COMPANY_THREAT_RESPONSE_CONTINUE_1_UNKNOWN = "Kdo volá?",
    RIVAL_COMPANY_THREAT_RESPONSE_CONTINUE_2_1 = "Dostaňte se k věci. Co chcete?",
    RIVAL_COMPANY_THREAT_RESPONSE_CONTINUE_2_2 = "Na tohle nemám čas. [ZAVĚSIT]",
    RIVAL_COMPANY_THREAT_RESPONSE_CONTINUE_3_1 = "Určitě s tím můžeme něco udělat.",
    RIVAL_COMPANY_THREAT_RESPOSE_HANG_UP = "[ZAVĚSIT]",
    RIVAL_COMPANY_THREAT_RESPONSE_CONTINUE_3_2 = "Co uděláte, zaměstnáte mě? [ZAVĚSIT]",
    RIVAL_COMPANY_LEGAL_ACTION_1 = "Víte, doufal jsem, že to nedojde až sem, ale nedáváte mi jinou možnost.",
    RIVAL_COMPANY_LEGAL_ACTION_2 = "Budu teď mluvit na rovinu, protože jste mě doopravdy naštval. Doufám, že máte dobrého právníka, protože se uvidíme za tu všechnu defamaci u soudu.",
    RIVAL_COMPANY_LEGAL_ACTION_3_A = "Máte dobrou pointu. Víte co? Oba jenom poškozujeme toho druhého, co set týče financí a reputace. Co kdybychom na tohle všechno zapomněli a k soudu nešli? Už žádné pomluvy, už žádné kradení zaměstnanců. Co říkáte?",
    RIVAL_COMPANY_LEGAL_ACTION_3_B = "Hodně štěstí, protože věřím, že můj případ je mnohem lepší.",
    RIVAL_COMPANY_LEGAL_ACTION_4_A = "Skvěle. Doufám, že už mezi námi už nebude nic špatného.",
    RIVAL_COMPANY_LEGAL_ACTION_4_B = "Jak chcete.",
    RIVAL_COMPANY_LEGAL_ACTION_RETALIATE = "Tuhle hru mužou hrát dva. Půjdu také k soudu, spolu s důkazy o vaších pokusech o defamaci.",
    RIVAL_COMPANY_LEGAL_ACTION_RECONSIDER_AGREE = "Souhlasím.",
    RIVAL_COMPANY_LEGAL_ACTION_RECONSIDER_DISAGREE = "V žádném případě.",
    PLAYER_LEGAL_ACTION_1 = "Dobrý den.",
    PLAYER_LEGAL_ACTION_2_A = "Počkejte chvíli, jste si jistý, že si chcete projít vším tím papírováním? Mám návrh - oba na všechno zapomeneme a začneme znovu. Už žádné pomluvy, už žádné přebírání zaměstnanců...Předstírejme, že se nic z toho nikdy nestalo.",
    PLAYER_LEGAL_ACTION_2_B = "Opravdu? Vzhledem k tomu, jak moc jste poškodil mou společnost, jsem přemýšlel o tom stejném. A když mi teď oznamujete, co uděláte...Myslím, že vám to oplatím. Uvidíme se u soudu.",
    PLAYER_LEGAL_ACTION_1_GENERIC = "Žaluji vás za defamaci. Připravte si právníka.",
    RIVAL_COMPANY_2_THREATEN_1 = "Dobrý den, tohle je ředitel 'PLAYER'.",
    RIVAL_COMPANY_2_THREATEN_2_EMPLOYEES = "Volám, protože jste přesvědčil jednoho z mých zaměstnanců, aby odešel z mé společnosti a připojil se k tvé vaší. Rozumím, že to je pro vás jenom byznys, ale nechci, aby mezi námi byl nějaký konflikt. Pro tentokrát vám to odpustím, když slíbíte, že to už nikdy neuděláte. Co říkáte?",
    RIVAL_COMPANY_2_THREATEN_2_SLANDER = "Volám, protože jste nedávno spustil proti mému studio pomlouvačnou kampaň. To bylo od vás velmi riskantní a stupidní. Pokud bylo vaším plánem vytvořit si nového nepřítele, uspěl jste.",
    RIVAL_COMPANY_2_THREATEN_3_AGREED = "Skvělé. Jsem rád, že jsme to mohli vyřešit.",
    RIVAL_COMPANY_2_THREATEN_3_DISAGREED = "No, teď nemůžete říct, že jsem vám nenabídl mír.",
    RIVAL_COMPANY_2_THREAT_RESPONSE_CONTINUE_1 = "Dobře. Co mohu pro vás udělat?",
    RIVAL_COMPANY_2_THREAT_RESPONSE_CONTINUE_1_UNKNOWN = "Ano, to jsem já.",
    RIVAL_COMPANY_2_THREAT_AGREE = "Dobře.",
    RIVAL_COMPANY_2_THREAT_DISAGREE = "Ne.",
    BACK_IN_THE_GAME_1_1 = "Zdravím a vítejte ve své nové kanceláři! Woah, déjà vu...vypadáte, jako kdybych vás už někde viděl. Každopádně, teď vám řeknu pár věcí o naší současné situaci...",
    BACK_IN_THE_GAME_1_2 = "Tato společnost už není to, co bývala. Před několika lety tu lidé vytvářeli skvělé hry, ale potom ředitel udělal několik špatných rozhodnutí a tohle studio je blízko bankrotu. Protože jsem do něj zainvestoval trochu peněz, nebyl bych příliš nadšený, kdybych o ně přišel.",
    BACK_IN_THE_GAME_1_3 = "A tady na scénu přicházíte vy. Slyšel jsem o vás dobré věci, proto jsem se rozhodl předchozího ředitele nahradit s vámi.",
    BACK_IN_THE_GAME_1_4 = "Váš úkol je relativně přímočarý - vrátíte toto studio do doby jeho dřívější slávy, což znamená, že dostanete počet jeho zaměstnanců zpět na šedesát a vrátíte mu reputaci, které dříve mělo.",
    BACK_IN_THE_GAME_1_5 = "A ještě jedna věc - vím že videohry jsou vaší vášní, takže pokud vám tohle vyjde, dám vám plnou svobodu na to, abyste si mohl dělat to, co se vám zachce. Ale pouze v případě, že toto studio zachráníte před jistou smrtí.",
    BACK_IN_THE_GAME_TIMELIMIT_BOTH = "Crr! Čas vypršel. YEARS let už uplynulo, ale nevidím ani šedesát zaměstnanců, ani nijak významné věci o tomto studio. Promiň příteli, ale byznys je byznys.",
    BACK_IN_THE_GAME_TIMELIMIT_REP = "Crr! Čas vypršel. YEARS let už uplynulo, ale stále jsem neslyšel nic nijak významného o tomto studio. Promiň příteli, ale byznys je byznys.",
    BACK_IN_THE_GAME_TIMELIMIT_EMPLOYEES = "Crr! Čas vypršel. YEARS let už uplynulo, ale nevidím těch šedesát zaměstnanců, na kterých jsme se domluvili. Promiň příteli, ale byznys je byznys.",
    BACK_IN_THE_GAME_GAMEOVER = "Investor vás nahradil s někým jiným, protože jste nezvládli dokončit všechny cíle včas. (dosáhnout REPK reputace & mít EMPLOYEES zaměstnanců)",
    BACK_IN_THE_GAME_2_1 = "Víte, když jsem vás poprvé do tohoto studia dosadil jako ředitele, myslel jsem, že navzdory vašemu snažení se mu bankrot nevyhne. Ale vypadá to, že jsem se mýlil! Podařilo se vám překonat očekávání má a ostatních investorů! Jak jsem řekl, teď máte plnou svobodu dělat si, co budete chtít. Gratuluji!",
    CONSOLE_DOMINATION_1_1 = "Zdravím! Je skvělé se konečně setkat s tím nejlépe kvalifikovaným kandidátem! Pojďme rovnou na věc...Není pochyb, že se zajímáte o herní průmysl a vývoj videoher. Kvůli tomu tu ale nejste.",
    CONSOLE_DOMINATION_1_2 = "Jste tu, protože přestože vytváření skvělých her může být dobrým zdrojem příjmu, vývoj zařízení, na kterých ty hry běží, může vydělat ještě více.",
    CONSOLE_DOMINATION_1_3 = "Řeknu vám to na rovinu - možná se vám dříve podařilo vytvořit jednu či dvě skvělé hry, ale vytvořit kompletně novou platformu je úkol úplně jiného kalibru. Čára balancu mezi kompletním selháním a úspěchem je u platforem opravdu tenká...tak tenká, že dělá problém ji vůbec vidět.",
    CONSOLE_DOMINATION_1_4 = "Všechna kritéria pro vedení vývoje platforem se vám podařilo splnit ze všech kandidátů nejlépe, a protože nejsem zrovna ta nejkvalifikovanější osoba, co se týče hardwaru, předám vás do rukou mého obchodního partnera zde.",
    CONSOLE_DOMINATION_2_1 = "Dobrý den! Pustíme se rovnou do detailů. K vytvoření platformy není potřeba jenom vybrat komponenty, ale také určit její cenu, výši licenčního poplatku pro herní vývojáře a různé jiné věci.",
    CONSOLE_DOMINATION_2_2 = "Protože tohle bude vaše první platforma, měl by jste vybrat ty nejlevnější komponenty, určit dobu vývoje a - pokud chcete - vybrat hlavního architekta.",
    CONSOLE_DOMINATION_2_3 = "Neočekávejte, že bude možné prodávat platformu s obrovskou marží. Ve většině případech si za každou prodanou platformu vyděláte jen pár dolarů.",
    CONSOLE_DOMINATION_3_1 = "Teď, když je všechno připraveno, se přesuneme k první části vývoje - design hardwarové architektury. V tomto bodě budeme prototypovat a zkoušet dát věci dohromady co nejefektivněji.",
    CONSOLE_DOMINATION_3_2 = "Protože zatím neexistuje žádná specifikace hardwaru, nemůžete pro platformu prozatím vytvářet žádné hry. Doporučil bych vám nějakou dobu počkat, než budeme mít základní design. Nebo vytvořte engine, který budete pro vývoj her používat.",
    CONSOLE_DOMINATION_4_1 = "Tám mi oznámil, že základní design hardwaru je hotov.",
    CONSOLE_DOMINATION_4_2 = "Pamatujete, jak jsem vám říkal, že z každé prodané platformy si vyděláte maximálně několik dolarů? To je místo, kde na scénu nastupují herní vývojáři. Protože už máme specifikaci hardwaru, je načase, abyste začal hledat ty, kteří budou ochotní vytvářet pro vaši platformu hry.",
    CONSOLE_DOMINATION_4_3 = "Navíc už může být dobrým nápadem začít pracovat na vlastní hře.  Nemusí jít o exkluzivitu, zejména protože jde o vaši první platformu.",
    CONSOLE_DOMINATION_4_4 = "Tak či tak, raději před vydáním platformy počkejte, než budou všechny hry dokončeny. Dostatek dostupných her her hned na dni vydání může udělat dobrý první dojem.",
    CONSOLE_DOMINATION_5_1 = "Fantastické. Teď tomu dejte nějaký čas, než ti zaujatí ve vytváření her pro vaši platformu odpoví. Poté budeme pokračovat.",
    CONSOLE_DOMINATION_6_1 = "Dobře. S herními vývojáři v našem týmu můžeme pokračovat dále. Zatímco budeme pracovat na finalizaci hardwaru, herní vývojáři budou vyvíjet hry pro den vydání.",
    CONSOLE_DOMINATION_6_2 = "Mějte na mysli, že vývojáři se rozhodli s vámi pracovat jen za současné ceny platformy. Pokud ji navýšíte, budete si muset najít nové vývojáře.",
    CONSOLE_DOMINATION_6_3 = "A ještě jedna věc - pokud navýšíte cenu platformy příliš vysoko, potenciální vývojáři pravděpodobně spolupráci s vámi odmítnou. Důvod je jednoduchý - nebude to pro ně bezpečná investice.",
    CONSOLE_DOMINATION_7_1 = "Platforma je připravena. Teď ji musíte jen vydat.",
    CONSOLE_DOMINATION_7_2 = "Nezapomeňte se ujistit, že všechny hry jsou už dokončeny, takže na dni vydání nebude jejich nedostatek. První špatný dojem může být pro platformu katastrofální.",
    CONSOLE_DOMINATION_8_1 = "Excelentní! Lidé si teď začnou vaši platformu kupovat. Nebuďte překvapen, pokud bude zpočátku ztrátová.",
    CONSOLE_DOMINATION_8_2 = "Pokud jste se s cenami nerozhodl být příliš chamtivý a platforma má dostatek decentních her, pak by prodeje měly vynahradit náklady na výrobu a opravy.",
    CONSOLE_DOMINATION_8_3 = "Pokud si myslíte, že jste nastavil přiliš agresivní cenu, můžete ji vždy snížit. Pamatujte, že navýšení cen po vydání naštve potenciální zákazníky více než jejich snížení, což může dlouhodobě vést k nižším prodejům a ušlým ziskům.",
    CONSOLE_DOMINATION_9_1 = "No, a protože jsme už platformu vydali, má práce zde je hotova. Teď budete muset komunikovat s naším přítelem investorem. Já se zase vrátím zpět k práci v hardwarových laboratořích, protože tam se hodím více.",
    CONSOLE_DOMINATION_9_2 = "Pokud se ohledně platformy stane něco důležitého, poradím vám, jak pokračovat.",
    CONSOLE_DOMINATION_9_3 = "Ještě jeden tip, který vám můžu dát - vytvořte pro svoji platfomu více her, protože tak získáte z každého prodeje 100% všech profitů a učiní ji to více atraktivní pro ostatní, což zase navýší prodeje. Je to win-win situace.",
    CONSOLE_DOMINATION_9_4 = "Musíte být ale realistický. Pokud bylo vydání špatné a zdá se, že platforma již nemůže být zachráněna, pak může být nejlepší pro ni zrušit podporu. Ne hned, ale až uplyne nějaké množství času.",
    CONSOLE_DOMINATION_10_1 = "Pojďme rovnou k věci. Zatímco vydělávat peníze je skvělé, není to důvod, proč tu jste. Tohle je sice naše první platforma, ale to neznamená, že ostatní investoři nebudou tvrdí.",
    CONSOLE_DOMINATION_10_2 = "Vaším cílem bude následující: vydělat během TIME z platforem MONEY$. Jen z platforem - vaše hry se počítat nebudou. Budou se ale počítat hry jiných vývojářů.",
    CONSOLE_DOMINATION_10_3 = "Snad nepotřebuji opakovat, že platformy jsou skvělým způsobem, jak vydělat peníze. Musíte to ale udělat tak, že zdrojem toho obrovského příjmu budou vaše platformy, ne hry.",
    CONSOLE_DOMINATION_10_4 = "Doufám, že jsme teď na stejné stránce, protože jste to vyl přece vy, kdo se pro tuto práci přihlásil! Hodně štěstí, a doufám, že za TIME nebudete mít stále prázdné ruce!",
    CONSOLE_DOMINATION_TIMELIMIT = "No příteli, uplynulo už TIME a těch MONEY$ nemůžu nikde najít. Je nešťastné, že tato obchodní příležitost nevyšla. Je mi líto, ale je konec.",
    CONSOLE_DOMINATION_GAMEOVER = "Investor vás nahradil s někým jiným, protože jste nezvládli vydělat během TIME MONEY$.",
    CONSOLE_DOMINATION_DEV_BUYOUT_1 = "Vítejte na ošklivé straně hardwarového průmyslu. Snad si nebudete nic brát osobně, protože pro ostatní výrobce a vývojáře je to jen byznys, takže se pokusí hned využít každé výhodné příležitosti.",
    CONSOLE_DOMINATION_DEV_BUYOUT_2 = "Nemůžete dělat nic jiného, než nabízet vývojářům výhodnější podmínky než konkurence. Jinak řečeno, budete budet nějaké své finance použít na ovnivňení šancí v náš prospěch.",
    CONSOLE_DOMINATION_ARCHITECTURE_PROBLEMS_1 = "No, tohle je poněkud nešťastné a zároveň trapné, heh...",
    CONSOLE_DOMINATION_ARCHITECTURE_PROBLEMS_2 = "Tyto věci se někdy stávají. Bohužel s tím nic až na poskytnutí více času na vyřešení všech problémů s architekturou dělat nemůžeme.",
    CONSOLE_DOMINATION_FIRMWARE_CRACK_1 = "Oh, tohle je velký problém. Věci tohoto typu se příliš často nestávají, ale když se stejně stanou, zaručeně to ovlivní vaše prodeje.",
    CONSOLE_DOMINATION_FIRMWARE_CRACK_2 = "Jednou věcí, kterou můžete ke snížení šancí cracknutí firmwaru udělat, je poskytnutí více času pro vývoj platformy. Tím myslím více času, než je minimální potřebné množství. Tak bude mít vývojový tým více času najít a opravit všechny chyby a slabiny.",
    CONSOLE_DOMINATION_FINISH_1 = "Tak to vypadá, že jste opravdu byl ten nejlepší kandidát! Gratuluji, během námi daného času se vám podařilo vydělat MONEY$! Hádám, že lze dokázat skvělé věci, když se vyskytne šance na vytvoření nové platformy.",
    CONSOLE_DOMINATION_FINISH_2 = "Vykonal jste opravdu skvělou práci a nepochybuji, že si ostatní investoři myslí to stejné. Odteď je všechno jen na vás, takže si toto studio můžete už řídit tak, jak budete chtít. Ještě jednou - excelentní práce!",
    DEV_COMMENTARY_INTRO_1 = "Ahoj a vítejte u vývojářského komentáře Game Dev Studio! Jmenuji se Roman Glebenkov a jsem tvůrcem této hry.",
    DEV_COMMENTARY_INTRO_2 = "Předtím než začnu, chtěl bych vám poděkovat za nákup Developer Commentary DLC. Zatímco komentář samotný je dostupný pro všechny, jeho namluvení je přístupné jen pro ty, co si DLC koupili. Důvod k tomuto se jednoduchý - nerad segreguji herní funkce do DLC. Aktualizace, které tato hra zdarma již obdržela, přidaly různé nové mechaniky.",
    DEV_COMMENTARY_INTRO_2_NO_NARRATION = "Komentář je dostupný pro všechny, ale jeho namluvená verze je může být koupena zvlášť jako DLC za 1.99 dolarů. Důvod k tomuto se jednoduchý - nerad segreguji herní funkce do DLC. Aktualizace, které tato hra zdarma již obdržela, přidaly různé nové mechaniky.",
    DEV_COMMENTARY_INTRO_3 = "Lidé říkali, že se všemi aktualizacemi, které hra obdržela, by měla stát více, ale zvedat cenu se mi nechtělo. To znamenalo, že jsem musel jít cestou, díky které by lidé, kteří mě chtějí podpořit, za to dostali něco zpět. To vedlo k vytvoření tohoto DLC.",
    DEV_COMMENTARY_INTRO_4 = "Komentář neposkytuje žádné nové herní mechaniky, ale můžete ho shledat zajímavým, ať už jste vývojářem nebo fanouškem, kterého zajímá, jak tato hra byla vyvinuta.",
    DEV_COMMENTARY_INTRO_5 = "Jak budete postupovat tímto scénářem a spouštět růžné události, budou se otevírat dialogy, které podrobně vysvětlí, jak byly herní mechaniky vyvinuty, jakými iteracemi si prošly a jiné věci, které možná shledáte zajímavými.",
    DEV_COMMENTARY_INTRO_6 = "Než se hrou začnete, chtěl bych něco říct o obtížnostech, ze kterých si vždy na začátku nové hry vybíráte.",
    DEV_COMMENTARY_INTRO_7 = "Ve verzi 1.0 žádné obtížnosti neexistovaly. Důvodem bylo hlavně přání, aby hráči zažili hru a její obtížnost tak, jak bylo zamýšleno.",
    DEV_COMMENTARY_INTRO_8 = "To jsem ale netušil, že jde o špatný nápad, a to i v simulátoru. Když byla hra vydána, vzniklo ohledně vysoké obtížnosti hry mnoho stížností. Někteří začali kvůli tomu i zveřejňovat negativní recenze.",
    DEV_COMMENTARY_INTRO_9 = "To znamenalo, že pokud jsem nechtěl vidět ještě více negativních recenzí, musel jsem něco udělat. A tak jsem se rozhodl přidat menu pro výběr obtížnosti, protože jsem si řekl, že když si osoba koupí nějakou hru, měla by mít možnost si vybrat její obtížnost.",
    DEV_COMMENTARY_INTRO_10 = "Nechtělo se mi to dělat, protože jsem chtěl, aby lidé hráli hru tak, jak jsem zamýšlel, ale bylo to něco, co jsem musel ke snížení množství negativních recenzí udělat.",
    DEV_COMMENTARY_INTRO_11 = "'Těžká' obtížnost má k té původní nejblíž. Říkám 'nejblíž' protože je stále snadnější.",
    DEV_COMMENTARY_INTRO_12 = "Důvodem je změna modelů prodejů a výplat a přidání půjček a licencování enginů.",
    DEV_COMMENTARY_INTRO_13 = "I přesto ale stále poskytuje zamýšlenou úroveň obtížnosti, takže pokud jste ještě na téžké nehráli - alespoň ji vyzkoušejte.",
    DEV_COMMENTARY_INTRO_14 = "S tímto za námi bych chtěl jště říct, proč jsem se vlastně rozhodl Game Dev Studio vytvořit. Hrál jsem Game Dev Tycoon, a přestože se mi líbil nápad té hry, myslel jsem si, že její herní mechaniky jsou příliš povrchové. A tak jsem si pomyslel: 'udělám simulátor vývoje videoher, do kterého bych dal alespoň sto hodin!'. A to jsem také udělal.",
    DEV_COMMENTARY_INTRO_15 = "Za existencí této hry jiný důvod ani neexistuje, chtěl jsem prostě vytvořit něco, co bych si užil. Game Dev Studio mělo v době vytváření tohoto komentáře na Steamu 80% pozitivních rencenzí, což mě činí šťastným, protože jsem vytvořil něco, co si užili i jiní.",
    DEV_COMMENTARY_INTRO_16 = "A to by k intru komentáře bylo vše. Hrajte hru jako normálně - dialogy se budou spouštět po kažné první interakci s nějakým herním systémem.",
    DEV_COMMENTARY_BUILDING_1 = "Konstrukce kanceláří byla centrální mechanikou hry. Schválně jsem vyvinul stoly i jiné objekty tak, aby nemohly být použitelné, pokud jsou blokovány.",
    DEV_COMMENTARY_BUILDING_2 = "Bohužel si někteří neuvědomili, že to je tak schválně, aby museli přemýšlet o rozložení kancelářských prostor a pokládání všeho co nejefektivněji. Kvůli tomu začali psát negativní komentáře.",
    DEV_COMMENTARY_BUILDING_3 = "Ve Steam diskuzích mě někteří přímo požádali, abych tu část hry rovnou odstranil. Nejdříve to bylo skličující a snažil jsem se přijít se způsoby, jak konstrukci udělat méně striktní, ale nakonec jsem došel k závěru, že neobětuji svou vizi hry jenom kvůli tomu, že se někdo naštval, protože nemohl postavit kanceláře, které z hlediska rozložení nedávají žádný smysl.",
    DEV_COMMENTARY_BUILDING_4 = "Chtěl jsem hráče donutit, aby nad rozložením svých kanceláří přemýšleli. Je na ně totiž jen omezený prostor, a tak musíte položit vše co nejefektivněji, abyste získali co nejvyšší množství pracovních míst a zároveň vybalancovali množství věcí jako záchody, zdroje vody, kuchyně, a podobně.",
    DEV_COMMENTARY_BUILDING_5 = "Ovlivňovatel motivace 'Komfort' ve verzi 1.0 neexistoval. Myslím, že jsem ho přidal dva měsíce po vydání hry, protože lidé byli nespokojeni s tím, že v ní neexistovaly žádné dekorativní objekty.",
    DEV_COMMENTARY_BUILDING_6 = "V době vydání mě to ani nenapadlo. Byl jsem tak pohlcený ve vývoji mechanik souvisejících s vývojem her, že jsem tento aspekt hry ani nezvážil.",
    DEV_COMMENTARY_BUILDING_7 = "Krátce po vydání jsem přidal několik dekorací a ovlivňovatel motivace 'Komfort'. Je vtipné, že jsem si jejich důležitost uvědomil až poté, co jsem je přidal.",
    DEV_COMMENTARY_BUILDING_8 = "Bez těchto objektů vypadaly kanceláře nevýrazně a nenápaditě. Všude byly řady stolů a záchodů, možná i s kuchyní. To bylo vše.",
    DEV_COMMENTARY_BUILDING_9 = "Když jsem přidal dekorativní objekty a zahrál si hru s nimi, napadlo mě: 'Jak je možné, že mě to nenapadlo?'. Zpětný pohled je 20/20.",
    DEV_COMMENTARY_EXPANSION_1 = "Když jsem začal pracovat na Game Dev Studiu, originální myšlenkou bylo mít jednu velkou kancelář, kterou by bylo možné mnohokrát rozšířit. V podstatě proměňování malé kanceláře v obrovskou. V praxi to ale příliš dobře nefungovalo, protože kancelář nevypadala hezky a lidé, kteří hru testovali, skončili se škaredými kancelářemi.",
    DEV_COMMENTARY_EXPANSION_2 = "Celkově měla budova čtvercový nebo obdelníkový tvar, což umožňovalo vytvořit obrovské řady pracovních míst s pár záchody mezi nimi. Nevypadalo to vůbec pěkně a nenutilo to hráče příliš přemýšlet, protože tvar byl vždycky stejný.",
    DEV_COMMENTARY_EXPANSION_3 = "Proto jsme se rozhodl vytvořit několik oddělených kanceláří, což bylo to správné rozhodnutí. Najednou lidé přemýšleli o rozložení jejich prostor, které navíc už vypadaly lépe.",
    DEV_COMMENTARY_EXPANSION_4 = "Rozšiřování kanceláří bylo ve hře už od prvního dne. Konstrukce nových pater byla přidána až v Aktualizaci #30. Změnilo to nějak průběh hry?",
    DEV_COMMENTARY_EXPANSION_5 = "Před Aktualizací #30, jakmile vám došlo místo v budově, museli jste koupit novou. To ale obvykle stálo majlant, což znamenalo, že expanze byla mnohem složitějším a strategičtějším krokem: pokud jste měli stěží dostatek peněz, existovalo velké riziko, že prostě zbankrotujete.",
    DEV_COMMENTARY_EXPANSION_6 = "Přidání více pater tohle změnilo. Jedno patro stojí jen zlomek toho, co nová budova, takže expanze je mnohem snadnější, méně riskantní a dostupnější. Navíc se maximální počet zaměstnanců několikrát navýšil.",
    DEV_COMMENTARY_EXPANSION_7 = "Tento nárust znamenal, že bylo nutné hru optimalizovat ještě více. Miluji optimalizování kódu, a tak jsem během vývoje nových aktualizací vždy hledal věci, které by šly v kódu zefektivnit. Aktualizace #29 přinesla kvůli budoucí akomodaci více zaměstnanců zhruba o 30% vyšší výkon.",
    DEV_COMMENTARY_EXPANSION_8 = "V Aktualizaci #30 jsem zašel ještě dále a optimalizoval oblasti, o kterých jsem věděl, že by z nich ještě trochu výkonu vymáčknout šlo. To přineslo další zlepšení, tentokrát 56% oproti předchozí aktualizaci. Přesně to, co jsem pro implementaci více pater potřeboval.",
    DEV_COMMENTARY_EXPANSION_9 = "Nechápejte mě špatně, hra běžela už tak decentně, ale vždy si myslím, že pokud lze výkon hry zlepšit, pak by to měl vývojář udělat. Ne každý má nejnovější a nejlepší počítač, takže dobrý výkon bylo něco, k čemu jsem se cítil zavázaný to poskytnout.",
    DEV_COMMENTARY_EXPANSION_10 = "Nakonec to stálo za to. Jistý fanoušek hry se spíše slabým počítačem řekl, že se výkon hry zlepšil z 30 FPS na solidních 60 FPS. To je pro low-end počítač fantastický výsledek.",
    DEV_COMMENTARY_EXPANSION_11 = "Největší optimalizace přišly z aktualizací #23, #29 a #30. Aktualizace #29 a #30 specificky přinesly optimalizace nutné pro kanceláře s více patry. Celkově, v závislosti na scénáři a s absolutně všemi optimalizacemi dohromady, teď hra běží až třikrát lépe než dříve, alespoň co se týče CPU.",
    DEV_COMMENTARY_EXPANSION_12 = "Funkce samotná - tedy více pater v kancelářských budovách - byla něčím, co jsem chtěl přidat ještě před vydáním hry. Nakonec jsem ji ale odložil, protože jsem si myslel, že hra už má dostatek obsahu spojeném s konstrukcí kanceláří.",
    DEV_COMMENTARY_EXPANSION_13 = "Tu funkci jsem se rozhodl přidat v Aktualizaci #30. Stálo to za to, protože se každému libí, dělá hru snadnější a celkově je perfektní pro lidi, kteří chtějí strávit méně času staráním se o co nejefektivnější rozložení nábytku a více času vytvářením kanceláří, které vypadají lépe.",
    DEV_COMMENTARY_EXPANSION_14 = "Možnost více pater je prostě funkce, o které jsem si do její implementace neuvědomil, jak moc hru zlepší.",
    DEV_COMMENTARY_ACTIVITIES_1 = "Nápad za aktivitami byl poměrně jednoduchý - herní vývojáři jsou normální lidé s potřebami a zájmy jako kdokoliv jiný. Proto by tu měly být team-buildingové aktivity, které by navýšily motivaci zaměstnanců.",
    DEV_COMMENTARY_ACTIVITIES_2 = "Přidat něco takového mě napadlo poté, co jsem se svými bývalými kolegy šel v roce 2015 nebo 2016 hrát paintball. V té době jsem stále pracoval jako herní vývojař profesně.",
    DEV_COMMENTARY_ACTIVITIES_3 = "Tato funkce je přímo vázána na znalosti, které zaměstnanec má. Hraní hodně airsoftu vám dá nějaké znalosti o střelných zbraních, praktikování bojových umění vás seznámí s bojováním, a podobně. Neintegrovat tyto systémy dohromady by byla uniklá příležitost.",
    DEV_COMMENTARY_ACTIVITIES_4 = "Na dni vydání ale neexistoval žádný pořádný důvod, proč aktivity používat. Ovlivňovaly motivaci příliš málo, aby se s nimi někdo obtěžoval a množství znalostí, které poskytovaly, bylo tak nízké, že k nějakým pořádným ziskům bylo potřeba je pořád spamovat.",
    DEV_COMMENTARY_ACTIVITIES_5 = "Myšlenkou, která za tím stála, byla taková: expertem na něco se nestanete jen během šesti měsíců. Celý proces trvá mnohem déle.",
    DEV_COMMENTARY_ACTIVITIES_6 = "Bohužel jsem ale aplikoval logiku skutečného života tam, kde to nebylo potřeba.",
    DEV_COMMENTARY_ACTIVITIES_7 = "V Aktualizaci #22 jsem se rozhodl množství získané motivace navýšit čtyřikrát, díky čemuž dávalo už větší smysl team-buildingové aktivity pořádat.",
    DEV_COMMENTARY_ACTIVITIES_8 = "Aktualizace #30 zase drasticky navýšila množství získaných znalostí. To znamenalo, že bylo mnohem rychlejší a snadnější je získávat. S tím byl ale spojen jeden jasný problém - hra se stala snadnější.",
    DEV_COMMENTARY_ACTIVITIES_9 = "Balancování tolika věcí není jednoduché. Nechtěl jsem, aby ze hry bylo 'Dark Souls žánru simulátorů vývoje her', jak jí už někteří říkali, ale zároveň jsem ji nechtěl učinit příliš snadnou.",
    DEV_COMMENTARY_ACTIVITIES_10 = "Nakonec jsem si prostě řekl 'kašlu na to' a rozhodl se učinit hru zábavnější pro nové hráče i za cenu snížení obtížnosti. Nakonec to stálo za to. Množství masochistických hardcore hráčů je mnohem nižší než počet těch, kteří si chtějí hru spíše užít než do detailů zjistit, jak každá funkce funguje.",
    DEV_COMMENTARY_EMPLOYEES_1 = "Když jsem začal na Game Dev Studiu pracovat, vyvíjel jsem ho se základní myšlenkou, že zaměřená bude na zaměstnancích. To znamenalo, že bych udělal zaměstnance v procesu vyvíjení her velmi důležitými.",
    DEV_COMMENTARY_EMPLOYEES_2 = "Protože hry jsou vytvářeny obyčejnými lidmi, chtěl jsem, aby si jich hráči vážili více než v jiných hrách. Proto jsem vytvořil dialogový systém a proto se občas stávají nemocnými, rozhodnou se jít pracovat jinam, a podobně.",
    DEV_COMMENTARY_EMPLOYEES_3 = "Nechtěl jsem, aby zaměstnanci byli jen pixely na obrazovce, které nemají žádnou jinou hodnotu kromě faktu, že můžou vyvíjet hry.",
    DEV_COMMENTARY_EMPLOYEES_4 = "To přirozeně znamenalo, že jsem jim musel dát více než dovednosti. Takže jsem implementoval kromě dovedností i jiné věci -  atributy, znalosti, vlastnosti a později specializace.",
    DEV_COMMENTARY_EMPLOYEES_5 = "Bylo tu ještě několik jiných věcí, o jejichž přidání jsem přemýšlel, ale rozhodl jsem se radši proti nim. Myslel jsem, že kdybych implementoval příliš mnoho atributů zaměstnanců, hra by se stala mikromanagementovým peklem.",
    DEV_COMMENTARY_EMPLOYEES_6 = "Jednou z těch nepřidaných věcí byly zkušenosti s designem, co se jednotlivých témat a žánrů týče. Na další věci si ale v tuto chvíli nemohu vzpomenout.",
    DEV_COMMENTARY_EMPLOYEES_7 = "Momentálně se musíte u každého zaměstnance starat o čtyři věci a už to je někdy únavné. Kdybych přidal ještě více, situace by byla ještě horší, tak jsem se rozhodl zastavit tam, kde jsme teď.",
    DEV_COMMENTARY_EMPLOYEES_8 = "Role zaměstnanců byly vytvořeny s myšlenkou, že většina zaměstnanců ve společnostech má jednu primární dovednost. Ve skutečném životě existuje jen málo lidí, kteří jsou profesionálními vývojáři, zvukovými designéry, grafiky, a podobně zároveň.",
    DEV_COMMENTARY_EMPLOYEES_9 = "Většina lidí se zaměří na jednu dovednost a na té potom pracují. Většina těch, kteří zkouší vše, nemůže být stejně dobrá jako ti, kteří jsou svému řemeslu oddaní.",
    DEV_COMMENTARY_EMPLOYEES_10 = "Takže máme manažery, softwarové inženýry, zvukové inženýry, a podobně. Herní vývojáři nejsou nějací zázrační pracovníci a každý se specializuje na něco jiného, protože je to to, v čem jsou nejlepší.",
    DEV_COMMENTARY_EMPLOYEES_11 = "Dovednosti jsou celkem přímočaré: zaměstnanci dělají pokrok v té své hlavní velmi rychle, pokud pracují na hrách a procvičují si ji.",
    DEV_COMMENTARY_EMPLOYEES_12 = "Schválně jsem u neprimárních dovedností udělal pokrok velmi pomalý pro kterékoliv neředitelské role, aby hráči neměli zaměstnance, co jsou experty na vše. To by nedávalo žádný smysl.",
    DEV_COMMENTARY_EMPLOYEES_13 = "Navíc by to učinilo hru příliš jednoduchou. Měli byste zaměstnance, co umí všechno na expertní úrovni. Kde v tom vězí výzva?",
    DEV_COMMENTARY_EMPLOYEES_14 = "Věk zaměstnanců vše doplňuje - jakmile se stanou dostatečně starými, odejdou do důchodu, takže budete muset pro ně najít náhradu. Ztráta zaměstnanců může být v raných stádiích hry spíše stresující, ale až si vyděláte nějaké množství peněz, je jen triviální, protože můžete najmout tatřka kohokoliv.",
    DEV_COMMENTARY_EMPLOYEES_15 = "Atributy byly také vyvinuty s myšlenkou, že je nelze mít všechny na maximální úrovni. Každý zaměstnanec má jen určité množství bodů atributů a je jen na vás, jak je přiřadíte.",
    DEV_COMMENTARY_EMPLOYEES_16 = "Každý atribut také přímo koresponduje nejlépe s určitými dovednostmi, takže jejich bezmyšlenkovité přiřazování je jen zbytečná ztráta. Chtěl jsem, aby o tom hráči přemýšleli.",
    DEV_COMMENTARY_EMPLOYEES_17 = "U znalostí platí to stejné jako u dovedností a atributů. Herní vývojáři jsou obyčejní lidé s vlastními zájmy a zálibami, takže budou přirozeně něco dělat ve svém volném čase.",
    DEV_COMMENTARY_EMPLOYEES_18 = "Například já jsem profesionálně pracoval jako herní vývojář téměř tři roky a přesto že jsem programátor, zajímá mě zvedání těžkých vah, kickbox a airsoft.",
    DEV_COMMENTARY_EMPLOYEES_19 = "Takže ve svém volném čase těmito zájmy trávím čas - zvedám těžké váhy, chodím do školy kickboxu a občas hraju airsoft. Chtěl jsem ukázat, že vývojáři videoher jsou obyčejní lidé a ne roboti, protože jsem si všiml, že mnoho lidé tomu nerozumí.",
    DEV_COMMENTARY_EMPLOYEES_20 = "Pamatuji si, jak někteří byli naštvaní, že není možné mít zaměstnance s každou dovedností na maximální úrovni. Tak ale život prostě funguje. Není možné být expertem v absolutně čemkoliv.",
    DEV_COMMENTARY_EMPLOYEES_21 = "Akivity tu jsou ke zvyšování úrovně znalostí, ale pokud ve skutečném životě zkusíte něco, co vás nějak nezajímá a nevěnujete se tomu, v dané oblasti nebudete příliš zběhlými. Stejná věc platí pro znalosti a aktivity v této hře.",
    DEV_COMMENTARY_EMPLOYEES_22 = "Vlastnosti jsou další věcí, kterou jsem implementoval, aby jsem zaměstnance učinil více 'živými'. Protože každá osoba je jiná a není perfektní, chtěl jsem u vlastností, aby to bylo něco za něco, nebo aby byly jen nepřímými zlepšeními.",
    DEV_COMMENTARY_EMPLOYEES_23 = "Je tu několik přimých zlepšujících vlastností, ale ty jsou věšinou omezené, takže pro svou postavu nemůžete vybrat absolutně všechny.",
    DEV_COMMENTARY_EMPLOYEES_24 = "U vytváření vlastních postav jsem chtěl hráče omezit hlavně na vlastnosti, u kterých je něco za něco, protože se stejně učí několikrát rychleji než jiné typy zaměstnanců.",
    DEV_COMMENTARY_EMPLOYEES_25 = "Normální zaměstnanci také mají přímé zhoršující vlastnosti: 'liný', 'nemotivován' a 'flákač'. Jenom způsobují pomalejší rychlost práce.",
    DEV_COMMENTARY_EMPLOYEES_26 = "To jsem implementoval, aby je hráči chtěli nahradit s těmi, co tyto vlastnosti neměli, což znamenalo vynaložit nějakou námahu na hledání vhodných náhrad.",
    DEV_COMMENTARY_JOBS_1 = "Pro najímání lidí máte dvě možnosti - buď zkontrolovat seznam těch, co hledají práci, anebo zveřejnit inzerát.",
    DEV_COMMENTARY_JOBS_2 = "Zveřejňování pracovních nabídek se stane dobrou alternativou teprve s pořádných množstvím meněz, protože hledání těch nejlepších zaměstnanců vyžaduje poněkud vysoké částky peněz.",
    DEV_COMMENTARY_JOBS_3 = "Game Dev Tycoon také nabízí možnost zveřejňovat inzeráty, ale částky potřebné v té hře jsou kompletně nerealistické a opravdu arkádové. To se mi v ní nelíbilo, takže pro Game Dev Studio jsem se rozhodl pro realističtější přístup.",
    DEV_COMMENTARY_JOBS_4 = "Vtipnou věcí je, že čím blíže realitě se během vytváření herních mechanik dostáváte, tím mnohem snadnějšími nebo těžšími se stávájí. Ohledně pracovních nabídek realismus odpovídal spíše jednoduchosti.",
    DEV_COMMENTARY_JOBS_5 = "Maximální cena na nabídku je stejně hrozně přemrštěná, ale rozhodl jsem se ji nechat na 50'000$, aby nebyl tento aspekt hry příliš jednoduchý ani příliš těžký.",
    DEV_COMMENTARY_JOBS_6 = "Ve skutečném životě na většině stránek zaplatíte za nabídku jen 300 - 500 dolarů. Rozhodl jsem se, že pro nalezení úplně nejlepších zaměstnanců bude nutné utratit velké množství peněz. Vysvětlením by mělo být to, že budete zveřejňovat nabídky na tolik stránek, jak je možné, což drasticky zvýši šanci na nalezení experta.",
    DEV_COMMENTARY_GAMES_1 = "Protože vytváření her je hlavním tématem hry, musel jsem se pokusit ho udělat co nejunikátnějším.",
    DEV_COMMENTARY_GAMES_2 = "Dovednosti a znalosti měly hrát ve vývoji her tu nejdůležitější roli. Dovednosti přímo přispívají ke kvalitě všech aspektů hry, zatímco znalosti jen tehdy, pokud mezi žánrem a tématem existuje nějaká shoda a pokud hra obsahuje relevantní funkce.",
    DEV_COMMENTARY_GAMES_3 = "Znalosti přispívají body kvality pouze v takových úkolech, kde to dává smysl. Znalosti ve zbraních přispívají ke hrám se zbraněmi, znalosti v boji přispívají k bojovým hrám, atd.",
    DEV_COMMENTARY_GAMES_4 = "Proto budete k získání co nejvyšší kvality hry a hodnocení potřebovat velmi zkušené zaměstnance s mnoha znalostmi.",
    DEV_COMMENTARY_GAMES_5 = "Enginy v vývoji her také hrají velmi důležitou roli. Zaprvé - úroveň výkonu může ovlivnit negativně hodnocení hry, pokud je příliš nízké. To dává z hlediska realismu smysl - ve skutečném životě existuje mnoho dobrých her, které ale běží opravdu příšerně a je těžké si je užít. Jsem si jistý, že určitě několik takových her znáte sami.",
    DEV_COMMENTARY_GAMES_6 = "Potom tu je jednoduchost použití enginu, která přímo ovlivňuje délku vývoje hry.",
    DEV_COMMENTARY_GAMES_7 = "Shody téma-žánr přidávají bonus k prodejům a výši hodnocení. Shody publikum-žánr také ovlivňují prodeje. U shod perspektiva-žánr platí to stejné. Existuje důvod, proč na trhu nenajdete mnoho strategií hraných z první osoby - ta kombinace prostě nefunguje dobře.",
    DEV_COMMENTARY_GAMES_8 = "Cena hry může kompletně zdevastovat vaše prodeje, pokud není nastavena správně. Další penalta je aplikována, pokud žánr hry nemá dobrou shodu s platformou, pro kterou byla vyvinuta...Chtěl jsem těchto faktorů vytvořit opravdu hodně, aby hráči museli přemýšlet co nejvíc.",
    DEV_COMMENTARY_GAMES_9 = "Věřím, že díky tomu se Game Dev Studio stalo mnohem strategičtější. Pamatuji si, jak jedna recenze říkala, že jde spíše o strategickou hru než o simulátor. Nevím, zda souhlasit s tou osobou, která to napsala, ale rozhodně můžu říct, že jsem rád, že té dané osobě se hra opravdu líbila.",
    DEV_COMMENTARY_GAMES_10 = "Randomizace shod žánr-téma plno věcí prostě vyhodí z okna. Pamatuji si, když jsemji přidal poprvé a zahrál si s ní - hra mi přišla jako kompletně nová. Byl jsem z toho naprosto nadšený, protože mi to přišlo, jako kdybych hrál zase poprvé.",
    DEV_COMMENTARY_ENGINES_1 = "Enginy jsou chtěl učinit co nejvíce znovupoužitelné. Nelíbilo se mi, že v Game Dev Tycoon musíte za přidávání funkcí k novému enginu platit peníze. Také se mi nelíbil fakt, že není možné engine aktualizovat s novými funkcemi. Nedávalo smysl, že implementace stejné funkce dvakrát stála více peněz než implementace jednou.",
    DEV_COMMENTARY_ENGINES_2 = "Aktualizování enginu s novými funkcemi některé jeho atributy zhorší, takže pokud jste ho aktualizovali znovu a znovu, budete ho muset dříve či později přepsat.",
    DEV_COMMENTARY_ENGINES_3 = "Logika za tím byla jednoduchá: když se k existujícímu enginu přidávají nové funkce, některé jeho části jsou přehlíženy. Výsledkem je chaotičtější kód, se kterým je těžší pracovat. Proto občasné předělávání enginů dávalo smysl. Proto jsou předělávání a aktualizování dvě rozdílné akce.",
    DEV_COMMENTARY_ENGINES_4 = "Licencování enginů nebylo ve verzi 1.0, ale bylo přidáno během měsíce po vydání. Tu funkci jsem spíše uspěchal, protože jsem měl obavy z ještě více negativních recenzí. To není pro hru na dni vydání nic dobrého.",
    DEV_COMMENTARY_ENGINES_5 = "I přes uspěchanou implementaci si myslím, že licencování enginů odvádí dobrou práci v poskytování extra zdroje příjmů. Jasně, nemá velkou hloubku, ale svou zamýšlenou akci provádí perfektně.",
    DEV_COMMENTARY_RIVALS_INTERACTION_1 = "Pro interakce s konkurenty jsem toho moc naplánovaného neměl. Přidal jsem je jen proto, aby bylo možné dělat i něco jiného než vytvářet hry. Nemít žádné překážky kromě těch ve vaší kanceláři by hru nečinilo tak zábavnou jako s nimi, protože do ní přidávají element překvapení a náhodnosti.",
    DEV_COMMENTARY_RIVALS_INTERACTION_2 = "Nápad na přidání konkurence jsem dostal, když jsem pracoval na scénáři 'Úvod', který se jmenoval ve verzi 1.0 'Příběhový režim' a byl původně tutoriálem.",
    DEV_COMMENTARY_RIVALS_INTERACTION_3 = "Mým nápadem bylo vybudovat kampaň kolem konkurování nepříjemnému konkurentovi v pozdních fázích scénáře.",
    DEV_COMMENTARY_RIVALS_INTERACTION_4 = "Pamatuji si, že jsem byl opravdu nadšený, když jsem dokončil implementaci všeho, co jsem pro interakce s konkurenty plánoval. Jako tvůrce hry jsem ale neměl tušení, jak zábavná ta mechanika bude pro někoho jiného, kdo bude hrát třeba poprvé. Jedinou věc, kterou si vybavuji, je že mě někdy dopálilo, když se mi konkurence během hraní hry rozhodla dát hůl do kola.",
    DEV_COMMENTARY_RIVALS_INTERACTION_5 = "Věděl jsem, že lidé by začali žádat o přidání možnosti kolaborovat s ostatními studii na hrách, ale to jsem nepřidal, protože jsem v tom zkrátka neviděl moc hloubky.",
    DEV_COMMENTARY_RIVALS_INTERACTION_6 = "Ve skutečném životě navíc na jedné hře pracuje více společností jen vzácně, a to ještě většinou v případech, kdy jsou všechny pod stejným vydavatelem. Přesvědčit několik náhodných společností, aby pracovaly na jednom projektu, mi přišlo nepravděpodobné.",
    DEV_COMMENTARY_RIVALS_INTERACTION_7 = "Hádám, že jsem mohl žaloby a takové věci trochu rozšířit, aby ve hře bylo více dramatu, ale neviděl jsem v tom prostě moc hloubky.",
    DEV_COMMENTARY_RIVALS_INTERACTION_8 = "Na druhou stranu, myslím, že tato posedlost s hratelností než takovými funkcemi je něco, co možná zabránilo hře, aby byla ještě lepší. V době psaní tohoto komentáře jsem z práce na Game Dev Studiu kompletně vyhořelý, takže vymýšlení nových herních mechanik je velmi obtížné a přijde mi spíše jako otrava. Možná vydám více aktualizací, až dokončím svou druhou hru.",
    DEV_COMMENTARY_RIVALS_SLANDER_1 = "Hlavním zaměřením pro konkurenty bylo jejich chování poté, co jim ukradnete zaměstnance. Psali by pomluvačné články a celkově se chovali jako kreténi.",
    DEV_COMMENTARY_RIVALS_SLANDER_2 = "Chtěl jsem, aby čas od času překazili vaše plány, pokud by jste si s nimi zahrávali. Nakonec jsem se ale rozhodl, že pomluvačné kampaně z jejich strany nebudou mít příliš velký efekt až na ztrátu reputace.",
    DEV_COMMENTARY_RIVALS_SLANDER_3 = "Pokud bych je implementoval víc hardcore, hra by se stala mnohem těžší.",
    DEV_COMMENTARY_RIVALS_COURT_1 = "Soudy mezi vámi a vašimi rivaly měly být ultimátním výsledkem zahrávání si mezi vámi.",
    DEV_COMMENTARY_RIVALS_COURT_2 = "Přemýšlel jsem o věcech jako sabotáž a espionáž, ale to nedávalo moc smyslu a nemyslím si, že ve skutečném životě existuje mnoho takových případů. Proto jsem takové věci nepřidal.",
    DEV_COMMENTARY_RIVALS_COURT_3 = "Na druhou stranu, přestože jich není v realitě tolik, žaloby nejsou mimo rámec skutečného života. Proto jsem je přidal do hry. Nenapadlo mě nic jiného, co by dávalo smysl z hlediska reality a zároveň by bylo zábavné.",
    DEV_COMMENTARY_RIVALS_BRIBES_1 = "Mezi tím vším přebíráním zaměstnanců, pomlouváním a žalobami tu muselo být ještě něco, co by odměnilo ty, co by si s konkurencí nezahrávali. Jako logickým řešením mi přišlo ptaní se na informace ohledně úplácení recenzentů.",
    DEV_COMMENTARY_RIVALS_BRIBES_2 = "Dávalo to smysl, protože pokud nemáte s nějakou společností špatný vztah, může být ochotná s vámi prostě sdílet informace.",
    DEV_COMMENTARY_RIVALS_BRIBES_3 = "Bylo to to nejmenší, co jsem mohl pro pozitivní vztahy mezi vámi a konkurencí implementovat.",
    DEV_COMMENTARY_GAME_EDITIONS_1 = "Herní edice byly přidány v Aktualizaci #30. Byla to funkce, se kterou jsem chtěl trochu okořenit proces vytváření her. Nápadem bylo přidat výhodný a zároveň riskantní způsob, jak získat více peněz a reputace.",
    DEV_COMMENTARY_GAME_EDITIONS_2 = "Pokud nastavíte u svých edicí správnou cenu a poskytnete jim dostatečnou hodnotu, potom vám zlepší prodeje, přidají extra reputaci a podobně. Pokud to ale pokazíte, můžete ztratit mnohem více reputace.",
    DEV_COMMENTARY_GAME_EDITIONS_3 = "Celkově vzato jde o jednu z mála funkcí, na kterou si lidé příliš nestěžovali, takže jsem ji asi implementoval správně. Není příliš trestající, ani příliš odměňující. Tak akorát.",
    DEV_COMMENTARY_PLATFORMS_1 = "Pro platformy jsem se rozhodl nejít historicky přesnou cestou, protože jsem chtěl celý trh simulovat.",
    DEV_COMMENTARY_PLATFORMS_2 = "Simulace trhu záleží jednak na tom, jaké hry vy a vaše konkurence pro platformy vytváříte, a jednak trochu na náhodnosti. Ta náhodnost je použita pro generaci falešných her, které jsou poté použity pro výpočet atraktivity platformy.",
    DEV_COMMENTARY_PLATFORMS_3 = "Hráč přirozeně nemůže konkurovat hromadě falešných her od neexistujících společností, což znamenalo, že hry od hráče a konkurence musely mít větší vliv na atraktivitu platformy.",
    DEV_COMMENTARY_PLATFORMS_4 = "Díky tomu systém funguje perfektně - pokud se budete držet jedné platformy a budete pro ni vytvářet mnoho her, její atraktivita raketově vzroste a v zásadě budete hrát roli v osudu všech ostatních platforem.",
    DEV_COMMENTARY_PLATFORMS_5 = "Pro statistiky platforem jsem se rozhodl jít cestou více spjatou s realitou, což znamenalo přidání popularity a licenčních poplatků založených na realitě. Věci jako obtížnost vývoje jsou 50/50 ohledně reality a spekulace. Například byl obtížné vyvíjet na Playstation 3, protože měl razantně odlišnou architekturu pro svůj procesor a grafiku od toho, co bylo na ostatních platformách.",
    DEV_COMMENTARY_PLATFORMS_6 = "Vtipnou věcí je, že i přesto je tato hra fiktivní, lidé byli stejně naštvaní tím, že jejich oblíbený výrobce konzolí zvůli simulaci trhu zbankrotoval.",
    DEV_COMMENTARY_PLATFORMS_7 = "Dokonce i ve fikci se lidé naštvou, když jejich oblíbený výrobce 'nevyhraje'. Nikdy jsem kvůli tomu nečekal negativní recenze, ale to jen ukazuje, jak jsou někteří hráči a recenzenti k některým věcem citově připoutaní. Je to něco, co stojí za zvážení, pokud jste herním vývojářem.",
    DEV_COMMENTARY_LOANS_1 = "Půjčování peněz ve hře ve verzi 1.0 nebylo a stejně jako v případě licencování enginů bylo přidáno brzy po vydání, v Aktualizaci #7. Lidé o tu funkci požádali ve Steam diskuzích.",
    DEV_COMMENTARY_LOANS_2 = "Zcela upřímně to mělo být ve hře hned po vydání. Je to další z funkcí, na které jsem před vydáním vůbec nepomyslel.",
    DEV_COMMENTARY_LOANS_3 = "půjčky byly absolutní nutností pro ty, co nejsou tak moc do simulátorů nebo si hru chtějí prostě užít než ji hrát do hloubky.",
    DEV_COMMENTARY_LOANS_4 = "Původně byly úroky půjček šíleně vysoké. 5% každý měsíc. To znamenalo 60% za rok. To je příliš vysoké. Myslel jsem, že 5% bylo pro krátká období v pořádku, ale neuvědomil jsem si, že to mělo být 5% za rok a ne za měsíc.",
    DEV_COMMENTARY_LOANS_5 = "Aktualizace #29 snížila úrokovou výši na 1%, což znamenalo 12% za rok. Aktualizace #30 ji ještě snížila, a to na 8% za rok. Teď to srovnejte s těmi původními 60%. Whoops.",
    DEV_COMMENTARY_PLATFORM_CREATION_1 = "Vytváření platform bylo přidáno v Aktualizaci #18. Bylo to něco, o co komunita požádala, a protože většina her v žánru tuto funkci měla, řekl jsem si 'sakra, proč ne?'.",
    DEV_COMMENTARY_PLATFORM_CREATION_2 = "Nebyla to funkce, kterou jsem plánoval přidat. O věci jako vytváření platform se moc nestarám, protože jsem se o ně nestaral ani v jiných hrách. Více jsem se zaměřil na vytváření her, takže proto tahle funkce nebyla ve hře od dne 1.",
    DEV_COMMENTARY_PLATFORM_CREATION_3 = "Má myšlenka myla jednoduchá - poskytnout další způsob, jak získat peníze a body reputace, ale udělat opravdu, opravdu obtížným vydělat si nějaké obrovské částky. Navíc by s tím bylo spjato poměrně vysoké riziko.",
    DEV_COMMENTARY_PLATFORM_CREATION_4 = "Tuto mechaniku jsem se rozhodl udělat tak obtížnou, protože je možné z ní získat velké množství peněz, pokud vše provedete správně. Také jsem se rozhodl zůstat realistický ohledně výrobních cen. Konzole jsou v současné době prodávány ve ztrátě pro výrobce, který si vydělává spíše přes prodeje her.",
    DEV_COMMENTARY_PLATFORM_CREATION_5 = "Lidé tomu znovu nerozuměli. Chtěli si vydělat peníze jak z platforem, tak z her na nich. V následujících aktualizacích jsem učinil vytváření plaforem mírně jednodušší, ale ceny komponent zůstaly nezměněné, takže zatímco bylo teď jednodušší na trhu uspět než dříve, byl to pořád gambling. K velkým ziskům by bylo pořád potřeba, aby jste udělali velkou část věcí správně.",
    DEV_COMMENTARY_PLATFORM_CREATION_6 = "Systém specialistů tu je, aby dal vaším platformám bonus, pokud jsou dostupní. Ten bonus je spíše velký a specifický na jednu oblast. Specialisté jsou během navrhování hardwaru důležití i v realitě - vezměte si třeba Jima Kellera. Je jedním z hlavních důvodů, proč CPU architektura Ryzen od AMD je tak dobrá.",
    DEV_COMMENTARY_PLATFORM_CREATION_7 = "Experts v hardwaru jsou velmi žádaní, takže jejich výplaty jsou přirozeně velmi vysoké. Pro Game Dev Studiu jsem odkazoval na realitu. Protože je po ich tak vysoká poptávka, učinil jsem některé specialisty pro některá období nedostupné. Navrhování hardwarové architektury znamená plno práce a námahy, takže znovu jsem se pro délky období, kdy jsou specialisté zaneprázdněni, znovu obrátil na realitu.",
    DEV_COMMENTARY_PLATFORM_CREATION_8 = "Platformy mají celkem čtyři části, aby hráči porozuměli, kolik práce do jejich vývoje jde. Samozřejmě jsem nešel do oblastí jako vlastní čipy, ale myslím, že jsem s tím jinak odvedl dobrou práci.",
    DEV_COMMENTARY_MMOS_1 = "MMO hry nebyly na dni vydání implementovány; cela funkcionalita byla přidána v Aktualizaci #14. Pokud si správně pamatuji, bylo to něco, co jsem do hry přidat chtěl. Myšlenka pro ně byla podobná jako u vlastních platform: vysoké riziko, vysoká odměna.",
    DEV_COMMENTARY_MMOS_2 = "Stejně jako u vývoje platforem jsem se rozhodl udělat úspěch u MMO her velmi těžko dosažitelným. Pokud se vám podaří uspět, pak si vyděláte značné množství peněz.",
    DEV_COMMENTARY_MMOS_3 = "MMO byla opravdu obtížná...a stále jsou. Vydal jsem sice několik aktualizací, které určité jejich aspekty učinily jednoduššími, ale stále jsem se držel původního nápadu vysoké obtížnosti.",
    DEV_COMMENTARY_MMOS_4 = "Před přidáním více pater byli hráči na pronájmu serverů spíše závislí. Bylo možné koupit vlastní servery a v kancelářích je rozmístit, ale nedostatek prostoru učinil obtížným spolehnout se jen na sebe. Stále je možné pronajmout si servery, ale díky mnohem většího množství dostupného místa to není tak potřeba jako dřív.",
    DEV_COMMENTARY_MMOS_5 = "Tohle je další vedlejší efekt přidání více pater, který mě nenapadl před jejich implementací. Je vtipné, jak přidání jedné takové funkce ovlivní tolik mechanik a přidá nové způsoby, jak hrát.",
    DEV_COMMENTARY_INTERVIEWS_1 = "Rozhovory byly vytvořeny s myšlenkou, že lze během nich říct dobré a špatné věci. I přesto jsem se ale rozhodl hráče za špatný rozhovor nepenalizovat. Místo toho jsem to udělal tak, že ze špatně vedeného rozhovoru toho moc nezískáte. V jiných slovech je to ztráta času.",
    DEV_COMMENTARY_INTERVIEWS_2 = "Není prakticky žádný způsob, jak takovou funkci udělat nějak komplexní, protože množství otázek je omezené. Jakmile ke každé najdete tu nejlepší odpověď, jakákoliv hloubka se jednoduše vytratí. Bylo by možné jich přidat více, ale to neřeší problém, který jsem právě popsal.",
    DEV_COMMENTARY_INTERVIEWS_3 = "Jednou možnou nápravou tohoto problému je každou otázku navrhnout tak, že bude mít nějaký důsledek na základě toho, co jste vybrali za odpověď. Když jsem na rozhovorech pracoval, nic takového mě nenapadlo, ale v budoucí aktualizaci možná s tím něco udělám.",
    DEV_COMMENTARY_INTERVIEWS_4 = "Nejlepším způsobem, jak implementovat rozhovory, by bylo vytvořit minihru, která s nimi nemá nic společného, například sbírání určitého množství něčeho v nějaké oblasti. To ale nebylo něco, co jsem chtěl udělat.",
    DEV_COMMENTARY_INTERVIEWS_5 = "Abych ten problém nějak omezil, rozhodl jsem se přidat možnost své hry během rozhovorů vyhypovat. Čím víckrát to uděláte, tím se sice očekávání všech pro hru navýší, ale zase dostane více hypu. pokud hra nedosáhne nebo nepřekročí tato očekávání, budete penalizováni. Pokud je hra dobrá - ne, skvělá - získáte mnohem více bodů reputace.",
    DEV_COMMENTARY_INTERVIEWS_6 = "Jenom tato funkce má větší hloubku než odpovídání na normální otázky.",
    DEV_COMMENTARY_REVIEWS_1 = "U recenzí jsem chtěl dát hráčům co nejvíce informací pro jejich příští hru, jak bylo možné. Recenze odhalí všechny shody a jediná věc, kterou hráč musí udělat, je si ji pročíst.",
    DEV_COMMENTARY_REVIEWS_2 = "Ne každá recenze odhalí něco nového, ale to se dá očekávat. Musíte hru hrát a učit se nové věci postupně, ne získat všechny informace naráz.",
    DEV_COMMENTARY_REVIEWS_3 = "Nemyslím si, že o tom moc lidí ví, ale množství bodů kvality, která jsou zobrazena nalevo od okna recenzí, zobrazují množství bodů kvality, která jsou potřeba k dosažení 100% skóre v tom specifickém aspektu.",
    DEV_COMMENTARY_REVIEWS_4 = "Jednou zajímavostí je, že kvůli systému recenzí někteří lidé zanechávali negativní recenze. Někteří si totiž mysleli, že pokud je například jejich skóre grafiky na 100% a zbytek na 30% - 40%, neměli by dostat hodnocení 5/10. Cítil jsem se těmito recenzemi trochu zklamán, protože někteří prostě očekávali, že by měli dostat určité hodnocení.",
    DEV_COMMENTARY_REVIEWS_5 = "Nesouhlasím s myšlenkou, že pokud jeden aspekt hry je fantastický a všechny ostatní špatné nebo průměrné, tak by hra měla i tak získat dobrá hodnocení. V realitě existuje mnoho her, které sice v jedné oblasti excelují, ale něco kompletně pokazí, což je činí méně zábavnými, než bylo zamýšleno.",
    DEV_COMMENTARY_REVIEWS_6 = "I tak jsem se ale rozhodl tento aspekt udělat pro hráče jednodušším, a tak jsem v jedné aktualizaci udělal to, že dobrá shoda žánr-téma hodnocení mírně navýší. Takže i přesto že vaše hra není tak dobrá, můžete stále získat bonusové skóre za dobrou shodu žánru s tématem.",
    DEV_COMMENTARY_BRIBES_1 = "Uplácení mělo něst vysoké riziko a nízkou odměnu. Není tu moc, co k tomu můžu říct. Osobně jsem proti úplatkům, stejně jako jakýkoliv jiný hráč. Přidal jsem uplácení jako 'posral jsi to, co sis sakra myslel?' typ věci. Je to spíš výsměch než cokoliv jiného.",
    DEV_COMMENTARY_BRIBES_2 = "Uplácením recenzentů si můžete vylepšit hodnocení hry, ale pokud úplatky přijme příliš mnoho z nich, lidé se stanou podezřívavými a ztratíte nějaké ty cenné body reputace.",
    DEV_COMMENTARY_BRIBES_3 = "Jak jsem již řekl, úplatky nejsou seriózní nebo validní herní mechanikou a hlavně existují jako vtip a jako něco, co vám může pokazit pokrok.",
    DEV_COMMENTARY_ADVERTS_1 = "U marketingu jsem chtěl plno možností. Hromadné reklamy mají mnoho možností, přičemž každá nejlépe funguje v určitých scénářích, má vlastní náklady, a podobně. Mým nápadem bylo dát hráčům co nejvíce možností, takže by si mohli vybrat ty, na které by měli a pak sklízet benefity.",
    DEV_COMMENTARY_ADVERTS_2 = "U reklam jsem se řídil cenami ve skutečném životě. Mnozí lidé si stále neuvědomují, jak drahý marketing je, a nerozumí, že je nejlepší spouštět reklamní kampaně během vývoje hry. Někteří se mě dokonce zeptali, abych ceny snížil.",
    DEV_COMMENTARY_ADVERTS_3 = "Lidí chtěli, aby reklamy byly ještě levnější než teď, dokonce i s posuvníkem rozpočtu.",
    DEV_COMMENTARY_ADVERTS_4 = "Možnost reklamních kol byla přidána v Aktualizaci #30 na základě žádosti od komunity. Stejně jako u mnoha jiných jsem i o této funkci příliš nepřemýšlel, když jsem ještě vyvíjel první verzi. Uvědomil jsem si její praktičnost až poté, co jsem ji přidal a zahrál si s ní.",
    DEV_COMMENTARY_ADVERTS_5 = "Let's playeři byli vytvořeni s myšlenkou, že většina lidí nesleduje internetové osobnosti, aby se rozhodla ohledně koupi nějaké hry. Jsou potřeba stovky tisíc zhlédnutí, aby to udělalo nějaký rozdíl. Většina sleduje takový obsah hlavně kvůli daným tvůrcům obsahu.",
    DEV_COMMENTARY_ADVERTS_6 = "Je vtipné, že se tato spekulace naplnila. Existuje několik videí o Game Dev Studiu s okolo 30 000 zhlédnutími, ale když jsem se podíval na statistiky prodejů, buď nebyly prodeje ovlivněny vůbec, nebo jen minimálně. Je to opravdu ironické.",
    DEV_COMMENTARY_ADVERTS_7 = "Shilling byl vytvořen s podobnou myšlenkou. Musíte vychvalovat produkt mnoha lidem, aby to nějak ovlivnilo prodeje. Jediným rozdílem mezi tímto a let's playi je ten, že shilling můžou předčasně ukončit autority, když na to přijdou.",
    DEV_COMMENTARY_ADVERTS_8 = "Přidal jsem tolik možností pro marketing jako reflekci toho, jakými způsoby jsem už viděl vývojáře propagovat své hry.",
    DEV_COMMENTARY_ADVERTS_9 = "Zveřejňování screenshotů nebylo ve verzi 1.0. Bylo přidáno v Aktualizaci #30, jako další způsob, jak získat na začátku hry téměř bez financí nějaký hype.",
    DEV_COMMENTARY_OUTRO_1 = "Vypadá to, že jste už narazili na vše, co tento komentář může nabídnout! Doufám, že bylo zajímavé se na hru podívat i z trochu jiného pohledu; na jají vývoj, na iterace, kterými si prošla a pochybami a těžkostmi, kterými jsem si prošel, když jsem ji aktualizoval.",
    DEV_COMMENTARY_OUTRO_2 = "Bylo zábavné tento komentář vytvořit a vyprávět. Chtěl bych vám ještě jednou poděkovat za nákup DLC Developer Commentary. Doufám, že kvalita byla uspokojující a že můj výklad udělal tento scénář zábavnějším.",
    DEV_COMMENTARY_OUTRO_2_NO_NARRATION = "Pokud si přejete podpořit mě a jakoukoliv budoucí hru, kterou vydám, určitě se podívejte na Developer Commentary DLC. Přidává k tomuto scénáři dabing, ale jde spíše o 'poděkování' od vás mně než o DLC. Stojí pouze 1.99 dolarů, přičemž po daních a podílu Steamu mi zůstane zhruba 50%. Takže pokud mi chcete koupit cheeseburger v McDonald's, rozhodně se na to DLC podívejte.",
    DEV_COMMENTARY_OUTRO_3 = "Game Dev Studio bylo v mém životě brovským milníkem. Jde o první hru, kterou jsem kdy dokončil a vydal, a to ve věku 23. S jejím vydáním jsem udělal mnoho chyb kvůli mé nezkušenosti, ale dost jsem se z nich naučil. Mnoho chyb, které v mých budoucích projektech už neudělám. Jsem vděčný za všechnu konstruktivní kritiku, kterou jsem od hráčů v recenzích dostal, protože její podstatná část pomohla tuto hru zlepšit do stavu, ve kterém je teď.",
    DEV_COMMENTARY_OUTRO_4 = "Se všemi aktualizacemi a obsahem, který jsem přidal a se všemi chybami, které jsem opravil, jsem si uvědomil, že Game Dev Studio mělo být nejdříve vydáno v rámci předčasného přístupu. Nebylo to ale něco, čemu jsem hned po vydání rozuměl, protože to byla má první vydaná hra. jenom jsem nečekal, že udělám chybu tak velkou.",
    DEV_COMMENTARY_OUTRO_5 = "Je snadné tady sedět a mluvit o všem, co jsem pokazil, je mi líto ten stav, ve kterém jsem hru vydal, ale tohle není něco, co by měl kdokoliv dělat. Dnes jsem pyšný na hru, kterou jsem vytvořil. Opravil jsem většinu všech chyb, přidal mnoho obsahu v aktualizacích zdarma a - což je nejdůležitější - po vydání jsem přidal všechny funkce, o kterých jsem si řekl, že je přidám.",
    DEV_COMMENTARY_OUTRO_6 = "Jednou věcí, které jsem si všiml, je to, že mladá generace hráčl chce dostat vše co nejdříve. Okamžité uspokojení, pokud se tomu dá tak říkat. Já pocházím z generace hráčů, během které hry jen zřídka dávaly vše, co jste chtěli. Museli jste zvážit, co si koupit a co jste mohli získat. Myslím, že to je jeden z halvních důvodů, proč jsem se rozhodl udělat Game Dev Studio zpočátku tak těžkou hrou a pravděpodobně jedním z hlavních důvodů, proč ji lidé dříve neměli tak rádi.",
    DEV_COMMENTARY_OUTRO_7 = "Na této hře není nic, co mě činí šťastnějšího než ty pozitivní recenze, které lidé zveřejňují, obzvláště pokud má daná osoba v ní nahráno 30, 50, 100 hodin. Pokud osoba strávila 100 hodin v něčem, co bylo koupeno za 9,99 dolarů (nebo méně, pokud ve slevě), tak vím, že jsem vytvořil opravdu zábavnou hru. Takže pokud se vám tato hra líbí, zvažte zanechání pozitivní reenze, pokud jste tak již neučinili. Pokaždé se nad nimi usměju, protože jse pročítám všechny.",
    DEV_COMMENTARY_OUTRO_8 = "Děkuji za to, že jste u tohoto komentáři vydrželi po celou dobu jeho trvání. Děkuji za koupení, hraní a užívání si Game Dev Studia. Zatím na viděnou!",
    PAY_DENBTS_1_1 = "Vítejte, vítejte! Slyšel jsem o vás již mnoho skvělých věcí! Nejdříve mě nechte vysvětlit situaci a co je nutné zde udělat...",
    PAY_DENBTS_1_2 = "Předchozí ředitel této společnosti vyvinul velmi ambiciózní hru. Jediným problémem je, že se prodávala opravdu špatně. Lidé, co do této společnosti něco zainvestovali - včetně mě - nebyli s tímto příliš šťastní, a tak jsme se ho rozhodli nahradit s někým, kdo se alespoň trochu vyzná.",
    PAY_DENBTS_1_3 = "Té hře se vedlo tak špatně, že předchozí ředitel musel prodat větší kancelářskou budovu jednomu z konkurentů, kteří v tomto průmyslu rostou opravdu rychle, a vyhodit velké množství zaměstnanců, aby se vyhnul bankrot.",
    PAY_DENBTS_1_4 = "Váš úkol je jednoduchý: budete muset během 20 let splatit dluh této společnosti. Celková suma je DEBT$. S vašimi dovednostmi ale pochybuji, že budete mít sebemenší problém.",
    PAY_DENBTS_1_5 = "Splatíte dluh a studio bude vaše, protože u mě je splacení dluhu takové velikosti poměrně dobrým indikátorem, že víte, co děláte.",
    PAY_DENBTS_TIMELIMIT = "No, příteli, dluh stále nebyl splacen a 20 let již uplynulo. Obávám se, že byznys je byznys, a protože kontrakt nemůžu změnit...budu vás muset nahradit s někým, kdo bude pro tento úkol vhodnější.",
    PAY_DENBTS_TIMELIMIT_GAMEOVER = "Investor vás nahradil s někým jiným, protože jste nedokázali splatit dluh DEBT$ během TIME$.",
    PAY_DENBTS_2_1 = "Aaaaa...konec! Dluh byl splacen a nepřekročil jste 20tiletý limit. Skvělá práce, opravdu skvělá! Odteď si se studiem můžete dělat, co chcete. Jsem si jistý, že lidem, co do této společnosti investovali, to vadit nebude. Gratuluji za vaši skvělou práci!",
    RAVIOLI_AND_PEPPERONI_COWORKER_1_1 = "Hej šéfe, dlouho jsme se neviděli.",
    RAVIOLI_AND_PEPPERONI_COWORKER_1_2 = "Není to tak špatné, ale chybí mi pracovat s ostatními. Tak rychlé rozpuštění 'Paxis' nebylo snadné přijmout, víte? Schází mi staré dny, kde jsme dělali skvělé hry bez téměř žádných zájmů korporací. Už mě přestalo bavit vyvíjet hry jen pro profit, takže jsem se rozhodl připojit k vašemu studiu.",
    RAVIOLI_AND_PEPPERONI_COWORKER_1_3 = "Opravdu? Škoda. Doufal jsem, že bych s vámi mohl zase pracovat. Pokud změníte názor, nějakou dobu budu ještě hledat práci, takže mě na nějakou dobu uvidíte v seznamu uchazečů o práci.",
    RAVIOLI_AND_PEPPERONI_COWORKER_ANSWER_1_1 = "NAME! Jak to jde?",
    RAVIOLI_AND_PEPPERONI_COWORKER_2_1 = "Hele, slyšel jsem, že jsi založil vlastní společnost.",
    RAVIOLI_AND_PEPPERONI_COWORKER_2_2 = "Mám-li být upřímný, chybí mi dny, kdy jsme jenom dělali dobré hry. Lidi v 'Arts of Electrics' jenom ruinují inovaci a zajímavé věci. Ve hrách, které mají jenom dělat profit, moc šarmu není, víte? Přijde mi, že bude lepší pracovat pro vás místo na hrách, které mě vůbec nezajímají.",
    RAVIOLI_AND_PEPPERONI_COWORKER_2_3 = "Taková škoda. Už mě nebaví to, co vytváří, takže pokud si to náhodou rozmyslíte - dejte mi vědět, nějakou dobu budu hledat práci.",
    RAVIOLI_AND_PEPPERONI_COWORKER_ANSWER_2_1 = "Je od tebe hezké nás navštívit. Jak to jde?",
    RAVIOLI_AND_PEPPERONI_COWORKER_3_1 = "Ťuk ťuk! Kdo tam? Sakra, dlouho jsme se neviděli!",
    RAVIOLI_AND_PEPPERONI_COWORKER_3_2 = "Celkem dobře, ale práce se stala poněkud vyčerpávající, obzvláště díky tomu, že některé novější projekty v 'Arts of Electrics', které tamní kravaťáci započali, nemají absolutně žádný charakter. Víte, zatím se vám daří celkem dobře na vaší vlastní pěst. Jsem si jistý, že pod vaším vedením bychom mohli zase vytvářet nějaké kvalitní hry.. Co říkáte, je ve vaší společnosti místo pro lidi jako já?",
    RAVIOLI_AND_PEPPERONI_COWORKER_3_3 = "Ah, tak možná příště. Doslova. Pokud si to rozmyslíte, dejte mi vědět - budu na vaši nabídku čekat.",
    RAVIOLI_AND_PEPPERONI_COWORKER_ANSWER_3_1 = "Hej! Jsem rád, že ses dostavil, jak se daří?",
    RAVIOLI_AND_PEPPERONI_COWORKER_4_1 = "Hej šéfe, vidím, že se vám celkem daří.",
    RAVIOLI_AND_PEPPERONI_COWORKER_4_2 = "Meh. Chybí mi, co náš tým dříve dělal. Teď je to jenom o korporátech, co chtějí co nejvyšší profit. Jasně, pořád rád vytvářím zvukové efekty, ale ty putují do her, které nemají duši. Proto tu jsem - řekl jsem si, že bych se možná mohl připojit k vašemu týmu? Rozhodně by to bylo lepší než pracovat pro 'Arts of Electrics'.",
    RAVIOLI_AND_PEPPERONI_COWORKER_4_3 = "Dobře. Jejich studio jsem už opustil, takže teď budu muset hledat práci jinde. Pokud si to rozmyslíte, budu čekat na vaši nabídku.",
    RAVIOLI_AND_PEPPERONI_COWORKER_ANSWER_4_1 = "Podívejme se, kdo to sem přišel! Pojď dál, jak se daří?",
    RAVIOLI_AND_PEPPERONI_COWORKER_5_1 = "Nazdar! Vídím, že tu máte celkem pěkné studio, příteli.",
    RAVIOLI_AND_PEPPERONI_COWORKER_5_2 = "Skvěle. Jenom mě vyčerpalo pracovat u 'Arts of Electrics', takže teď hledám novou práci. Což je i důvod, proč jsem sem přišel, haha! Chybí mi dny, kdy jsem dělali hry, které nás opravdu bavily hrát, takže jak se dívám na to, co tu se svými týmy děláte...Existuje nějaká šance, že by jste mě najali?",
    RAVIOLI_AND_PEPPERONI_COWORKER_5_3 = "Oh, to je škoda. Pokud změníte názor - dejte mi vědět a přijdu sem opravdu rychle.",
    RAVIOLI_AND_PEPPERONI_COWORKER_DECISION = "[...]",
    RAVIOLI_AND_PEPPERONI_COWORKER_ANSWER_5_1 = "Hej, jak se ti vede?",
    RAVIOLI_AND_PEPPERONI_COWORKER_ACCEPT = "Vítej zpět v týmu! [NAJMOUT]",
    RAVIOLI_AND_PEPPERONI_COWORKER_REFUSE_1 = "Je mi líto, ale teď není vhodná chvíle. [ODMÍTNOUT]",
    FAN_WANTS_TO_WORK_MORE_INFO = "OTEVŘÍT MENU S INFORMACEMI O ZAMĚSTNANCI",
    RAVIOLI_AND_PEPPERONI_COWORKER_MORE_INFO_RETURN = "[...]",
    INITIAL_CEO_RNP_INITIAL = "Ano?",
    CALL_CEO_RNP_INITIAL_2 = "Ah, to jsi ty! Slyšel jsem, že jsi nedávno založil vlastní společnost. Je škoda, co se nedávno stalo, ale určitě chápeš, že jde jen o byznys. Nemělo to dělat nic s tebou jako herním designérem, investoři měli jen obavy, že tvoje budoucí projekty budou jen finanční zátěží.",
    CALL_CEO_RNP_INITIAL_3 = "Vím, ale musíš pochopit, že se ta hra prodávala špatně. Pokud já a investoři víme, je to tvoje vlastní vina. S tím nemůžu nic udělat. A pamatuj, je to jen byznys, takže jsme se museli zbavit ztrát.",
    CALL_CEO_RNP_INITIAL_4_A = "Dobře, myslím, že jsi z celé situace ještě celkem rozžhavený. Promluvme si později, až trochu vychladneš.",
    CALL_CEO_RNP_INITIAL_4_B = "Neposloucháš, co ti říkám. Už jsem ti řekl, že jde jenom o byznys. Podívej, na tohle nemám čas, takže si znovu promluvmě, až nebudeš ve špatné náladě, dobře?",
    CALL_CEO_RNP_INITIAL_ANSWER_2 = "Zátěž? Lidé ji milovali, jenom jste tak málo investoval do marketingu, že nikdo ani nevěděl, že existuje!",
    CALL_CEO_RNP_INITIAL_ANSWER_3_A = "Tohle nemá s hrou nic společného. Pokud se prodávala špatně, je to jen vaší vinou, protože jste ji nepropagoval dostatečně dobře.",
    CALL_CEO_RNP_INITIAL_ANSWER_3_B = "Tak proč mě vyhodit? Navrhl jsem skvělou hru, tak proč?",
    RIVAL_COMPANY_THREATEN_RNP_1 = "Víte, doufal jsem, že to k tomuto nedojde...",
    RIVAL_COMPANY_THREATEN_RNP_2 = "Vím, že věci mezi námi nebyly dobré již dlouhou dobu, takže jsem měl podezření, že situace dospěje až sem. A hele, dospělo to! Kradení zaměstnanců nebo pomluvačné kampaně - vše je to stejné, ne?",
    RIVAL_COMPANY_THREATEN_RNP_3 = "Každopádně, nebudu se jen dívat, jak se pokusíte tuto společnost zničit. Fakt, že jsme byli dříve kolegy, mi je úplně ukradený. Zastanu se zájmů lidí, kteří této společnosti něco dali. Doufám, že máte dobrý plán pro ofenzívu nebo zálohu, protože to určitě budete potřebovat.",
    RIVAL_COMPANY_THREAT_RNP_RESPONSE_REPLY_1 = "Oh, jasně, alespoň dokud se tvoje reputace nezřítí jako domeček z karet nebo selžeš vydat hru včas a tvá finanční situace půjde do kytek. Potom uvidíš, pro koho bude lepší pracovat.",
    RIVAL_COMPANY_THREAT_RNP_RESPONSE_CONTINUE_1 = "Co chceš?",
    RIVAL_COMPANY_THREAT_RNP_RESPONSE_1 = "Pro vaše zaměstnance bude lepší, když budou pracovat pro mě.",
    RIVAL_COMPANY_THREAT_RNP_RESPONSE_2 = "Hele, nevidím důvod, proč se tak naštvat - je to jen byznys. [ZAVĚSIT]",
    RAVIOLI_AND_PEPPERONI_1_1 = "Ah, zdravím! Slyšel jsem, co se stalo v \"Arts of Electrics\". Nebuďte na sebe příliš tvrdý, nízké prodeje té hry nemají co dělat s její kvalitou, ale spíš s tím, že tým, co měl na starosti marketing, odvedl příšernou práci.",
    RAVIOLI_AND_PEPPERONI_1_2 = "Každopádně vím, čeho jste schopen. Být vyhozen bez někoho, kdo by vám hlídal záda, není příliš příjemné. Takže mám pro vás nabídku, a to takovou, kterou by odmítl jen blázen!",
    RAVIOLI_AND_PEPPERONI_1_3 = "Poskytnu vám malé místo na práci a nějaké finance. Jak to zní, eh? Skvěle, že? Věděl jsem, že se vám to bude líbit!",
    RAVIOLI_AND_PEPPERONI_1_4 = "Takže to je ta nabídka. Tohle nedělám kvůli tomu, že bych očekával něco na oplátku. Dělám to proto, protože jako herní designér jste celkem dobrej. Výsledky, kterých jste už dosáhl, jsou více než dostačující k tomu, aby mě přesvědčily, že ať už do vás nasypu kolik peněz, kolik budu chtít, jistě se mi to po nějaké době vyplatí.",
    RAVIOLI_AND_PEPPERONI_1_5 = "Takže teď jste hlavou svého vlastního studia. Možná že je zatím malé, ale s vašimi dovednostmi a reputací pochybuju, že to tak zůstane. A kdo ví, možná se vám podaří získat k sobě do týmu i některé ze svých bývalých kolegů? Hodně štěstí.",
    RAVIOLI_AND_PEPPERONI_2_1 = "No tohle, v \"Arts of Electrics\" fakt vyhlásili bankrot! Budu předstírat, že s tím nemáte co dočinění. Nemám tušení, jestli vám pogratulovat nebo se vás začít i trochu obávat, haha!",
    RAVIOLI_AND_PEPPERONI_2_1_B = "Takže jste se rozhodl je odkoupit, eh? No, hlavně že máte svou pomstu, ne? A s vámi jako ředitelem jsem si jist, že hry, které budou lidé pod vámi vyvíjet, budou ještě impresivnější než kdy dříve!",
    RAVIOLI_AND_PEPPERONI_2_2 = "Každopádně nepochybuji, že k tomuto bylo zapotřebí obrovské množství práce. Vybudoval jste pěkné studio a ještě se pomstil těm, co téměř zruinovali vaši kariéru. Gratuluji!",
    A_FORMIDABLE_REPUTATION_1_1 = "Zda se, že vyše studio nedávno vydalo hru, která není příliš dobrá.",
    A_FORMIDABLE_REPUTATION_1_2 = "Možná to zmiňuji trochu pozdě, ale vaše reputace se vydáváním špatných her nezlepší. Spíš naopak - bude z toho trpět.",
    A_FORMIDABLE_REPUTATION_1_3 = "Až začnete pracovat na nějakém projektu, ujistěte se, že ho zvládnete dokončit do čtyř let. Časem se totiž vydávají nové technologie a zvedají standarty her, takže pokud vaše hra běží na zastaralé technologii a nejde o nějaké mistrovské dílo, bude hodnocena negativněji.",
    AN_EYE_FOR_AN_EYE_1_1 = "Zdá se mi to snad, nebo se to opravdu stalo? Pokud ano, nejsem příliš překvapen. Vím ale, co si asi myslíte. Cítíte vztek, co?",
    AN_EYE_FOR_AN_EYE_1_2 = "Neberte si to osobně. Mějte na paměti, že pro ostatní společnosti v branži je to jen byznys.",
    AN_EYE_FOR_AN_EYE_1_3 = "To ale neznamená, že s tím nic nemůžeme udělat. Co kdybychom je dali ochutnat jejich vlastní medicínu? Podívejte se na zaměstnance, které mají, jednoho si vyberte a pošlete mu pracovní nabídku. Pak uvidíte...",
    AN_EYE_FOR_AN_EYE_2_1 = "Hahaha, to snad nemůže myslet vážně? Takový hypokrit! Nejdřív se vám pokusí ukrást zaměstnance a najednou je to vaše vina!",
    AN_EYE_FOR_AN_EYE_2_2 = "Pojďme se ale radši zaměřit na to, na čem momentálně záleží nejvíc - vyhládil vám de facto válku, takže vy dva v se v dohledné době rozhodně nejlepšími přátely nestanete.",
    AN_EYE_FOR_AN_EYE_2_3 = "Tak či tak, gratuluji, právě jste jeho počet zaměstnanců snížil o jeden! Jedna nula pro 'STUDIO'!",
    AN_EYE_FOR_AN_EYE_2_4 = "To ale nebude stačit na to, abychom ho vyřadili ze hry. Jako další krok by jste mu měl přebrat další dva zaměstnance.",
    AN_EYE_FOR_AN_EYE_2_5 = "Hlavně pamatujte. Je to jen byznys.",
    STORY_MODE_CALL_FROM_RIVAL_1 = "Víte, možná jste na něco kápl. Věděl jsem, že od vás získám nějakou reakci, ale nic na takové úrovni. Ukradl jste mi tři zaměstnance. Tři! Začínám si myslet, že možná, ale opravdu možná, budu muset podniknout nějaká mimořádná opatření...",
    AN_EYE_FOR_AN_EYE_3_1 = "No podívejte se na to! Podařilo se vám ho naštvat! Jaká že je pravděpodobnost? Začíná být celý dramatický, což znamená, že ho určitě napadají nějaké zlověstné plány.",
    AN_EYE_FOR_AN_EYE_3_2 = "Poradil bych vám pokračovat s prací tak, jak doposud. Pokud mu ještě chcete přebrat pár zaměstnanců - klidně. Jsem zvědav na to, s čím nakonec přijde!",
    AN_EYE_FOR_AN_EYE_4_1 = "Huh, zajímavé...Pomluvačný článek. Zdá se, že někdo pracuje tvrdě na tom, aby vám zničil reputaci.",
    AN_EYE_FOR_AN_EYE_4_2 = "Všechno je to až moc velká náhoda. Nejdřív je ředitel 'COMPANY' celý dramatický a najednou tu máme pomluvy. Nechte mě to trochu promyslet...",
    AN_EYE_FOR_AN_EYE_4_3 = "Hmm. Pokračujte s prací stejně, jako do teď. Pokud to byl opravdu on, určitě se kvůli pomstě nezastaví. Určitě bude pokračovat a stane se neopatrným...Začne za sebou zanechávat stopy...A po nějaké době budeme mít dost důkazů na to, abychom ho zažalovali. A až se stane tohle, může svou reputaci políbit na rozloučenou.",
    AN_EYE_FOR_AN_EYE_5_1 = "Ta zmije! Konečně jsme ho dostali! Už máme dost na to, abychom ho mohli zažalovat. Tohle začíná být vzrušující!",
    AN_EYE_FOR_AN_EYE_5_2 = "Zavolejte mu a řekněte mu, že už si připravujete právníka. Nedopusťte, aby vám to rozmluvil. Jde jenom o jedovatou zmiji.",
    AN_EYE_FOR_AN_EYE_6_1 = "No, a tady to máte. O jednoho konkurenta méně. To neznamená, že jste v bezpečí - znamená to jen to, že on si s vámi už rozhodně zahrávat nebude. Vlastně bych se i vsadil, že se jeho společnost žene k bankrotu! A až se to stane, většina jeho zaměstnancl bude hledat práci, jestli víte, co tím myslím...",
    AN_EYE_FOR_AN_EYE_6_2 = "Každopádně tady se rozejdeme a každý se vydáme vlastní cestou. Ušel jste dlouhou cestu a definitivně dokázal, že jste více než schopen spravovat své vlastní herní studio.",
    AN_EYE_FOR_AN_EYE_6_3 = "Pokud si budete přát pokračovat, klidně. Tohle studio je teď jenom vaše! Pokud ale budete chtít nové dobrodružství...víte, co tím myslím?",
    AN_EYE_FOR_AN_EYE_6_4 = "Není vůbec za co! Zdá se, že jste nakonec byl tou správnou volbou. Řekl bych, že se vám podařilo vybudovat celé impérium, ale to už přeháním, co?",
    AN_EYE_FOR_AN_EYE_6_5 = "Já a vy, obchodní partnere, jsme dosáhli celkem dobrého úspěchu. Hodně štěstí ve vašem budoucím podnikání!",
    AN_EYE_FOR_AN_EYE_FINAL_ANSWER_1 = "Určitě to byl skvěle strávený čas. Děkuji za to, že jste mi věřil.",
    AN_EYE_FOR_AN_EYE_FINAL_ANSWER_2 = "Na viděnou.",
    CLIMBING_THE_LADDER_1_1 = "Ka-ching! To je zvuk osmi set tisíc v bance! Věděl jsem, že o vás mám mít dobrý pocit!",
    CLIMBING_THE_LADDER_1_2_A = "Když už teď máte celkem decentní kapitál, vaším dalším krokem by mělo být najmout nějaké lidi. Třeba patnáct.",
    CLIMBING_THE_LADDER_1_2_B = "Řekl bych vám, ať pro svou kancelář najmete patnáct zaměsnanců, ale jak vidím, o tohle jste se už postaral, takže tento krok přeskočíme.",
    CLIMBING_THE_LADDER_1_3_A = "To ale neznamená, že by jste měl přestat dělat hry! Dávám vám 36 měsíců na vydělání dalších 500'000$.",
    CLIMBING_THE_LADDER_1_3_B = "Tak či tak, rozhodně by jste neměl přestat vytvářet hry. Až za 36 měsíců vyděláte dalších 500'000$ - nebo ne - řeknu vám, co dál.",
    CLIMBING_THE_LADDER_2_1 = "No jen se na to podívejte! Máte solidní množství lidí, kteří pro vás pracují, máte peníze a vytváříte hry. Zní to dobře, co?",
    CLIMBING_THE_LADDER_2_2 = "A taky že ano! Jediná věc, která vám teď schází, je reputace. Dobrá reputace navýší prodeje vaších her i bez nějakého velkého marketingu, protože lidé už ví, že vaše hry za nákup stojí.",
    CLIMBING_THE_LADDER_2_3 = "Takže dalším vaším cílem bude získat 20'000 bodů reputace. Nedám vám žádný časový limit, protože jste už ukázal, že víte, co děláte. Hlavně ať to ale netrvá příliš dlouho, nebo všichni zešedivíme, haha!",
    climbing_the_ladder_timelimit = "Čas vypršel. Dostal jste se sice daleko, ale vypadá to, že tohle je pro vás už moc. Je to sice nešťastné, ale budu vás muset nahradit někým jiným.",
    CLIMBING_THE_LADDER_TIMELIMIT = "Investor vás nahradil s někým jiným, protože se vám nepodařilo splnit jeden z jeho úkolů (vydělat 500'000$ a získat 15 zaměstnanců) do tří let.",
    FIRST_CONTACT_1_1 = "Pořád někde slyším příběhy o nějakém herním studiu, které se stává poněkud populárním. Mluvím o vás!",
    FIRST_CONTACT_1_2 = "Mít dobrou reputaci je dobré, protože více lidí si bude kupovat vaše hry a uchazeči o práci pravděpodobněji přijmou pozici ve vašem studiu.",
    FIRST_CONTACT_1_3 = "Všechno ale není tak růžové, jak by se mohlo zdát. Jsou tu i jiné herní společnosti, které neustále hledají talentované osoby. Některé chtějí uspět tak moc, že si klidně v průmyslu udělají několik nepřátel. Možná se zamyslí i nad přebráním některých vašich zaměstnanců.",
    FIRST_CONTACT_1_4 = "Takže až se někdo pokusí připravit vás o zaměstnance - dejte mi vědět. Budou toho pak litovat...",
    GETTING_STARTED_DIALOGUE_1 = "Dobře, příteli, vítejte ve svém novém domově. Zvaž tuhle investici jako kličem k vašemu budoucímu úspěchu.",
    GETTING_STARTED_DIALOGUE_2 = "Teď mi dlužíte, protože jsem do vás investoval nějaké své těžce vydělané peníze. Nějakou dobu budete proto muset následovat mé instrukce.\nTento malý prostor bude pro vás a možná další dvě další osoby stačit, takže teď byste z něj měl udělat něco užitečného.",
    ON_MY_OWN_DIALOGUE_1_1 = "Tak, a tady už trochu ubereme. Teď poslouchejte: sice vím, že vaším prvním krokem je dosáhnout hvězd, ale budete muset trochu zpomalit. Jste stále nováčkem, potřebujete peníze, více zaměstnanců a - což je nejdůležitější - vědět, jak spravovat svůj čas a zdroje.",
    ON_MY_OWN_DIALOGUE_1_2 = "Takže vaši cestu začneme s jednoduchým úkolem. Dávám vám čtyři roky na to, abyste vydělal 800'000$. Pokud to zvládněte, klidně do vás zainvestuju více peněz. Pokud ne, tak naše partnerství skončí a já najdu někoho, s kým vás nahradit.",
    ON_MY_OWN_DIALOGUE_1_3 = "Jsme teď na stejné stránce? Jsem rád, že ano! Jste nadšený ohledně budoucnosti? Hodně štěstí ve vašem prvním podniku, obchodní partnere!",
    ON_MY_OWN_DIALOGUE_1_4 = "A mimochodem, všiml jsem si, že jste stále nikoho nenajal. Doporučil bych vám, abyste najal alespoň jednoho softwarového inženýra. Zatím ale jen tak nenajímejte experty s velkými výplatami - toby vám došly peníze ještě před tím, než by se vám podařilo dokončit další hru!",
    ON_MY_OWN_DIALOGUE_2_1_A = "Teď poslouchejte. Na váš účet jsem převedl 500'000$. To je velká suma. Doufám, že to použijete na marketing své hry, aby se lépe prodávala, ale pokud s nimi chcete udělat něco jiného, klidně. Marketing by byl ale momentálně nejlepší!",
    ON_MY_OWN_DIALOGUE_2_2_A = "Dobrá hra se nebude přiliš prodávat, když o ní nikdo neví. Můžete udělat mistrovské dílo, ale pokud o něm nikdo nemá ani tušení, nebude se moc prodávat. A lidé, kteří si o ní budou jen tak povídat, také příliš nepomůžou, pokud se tedy nestane nějaká náhoda. Náhody se ale stávají jen zřídka...",
    ON_MY_OWN_DIALOGUE_2_1_B = "Řekl bych vám, abyste začal pracovat na marketingu vaší hry, až bude téměř dokončena, ale jak vidím, to už jste se svými finančními rezervami udělal. Bez ohledu na to ale na váš účet i tak převedu 500'000$. Měly být určeny na propagaci vaší hry, ale vzhledem k tomu, že to už jste učinil...",
    ON_MY_OWN_OFF_MARKET_DIALOGUE_1 = "Slyšel jsem, že vaše první hra byla stažena z prodejů.",
    ON_MY_OWN_OFF_MARKET_DIALOGUE_2 = "To se stane každé hře, kterou vy a vaše konkurence uděláte. Délka doby, po kterou hra zůstane trhu, záleži na tom, jak dobře se prodává. Pokud tedy uděláte dobrou hru, zůstane na trhu dlouho hlavně kvůli tomu, že se rychle prodává.",
    ON_MY_OWN_OFF_MARKET_DIALOGUE_3 = "V jiných slovech, neočekávejte, že si nějak moc vyděláte na špatné hře. Vynaložíte do hry námahu - získáte peníze; nevynaložíte ji - no, teď vám bude v peněžence zet díra. Jednoduché.",
    ON_MY_OWN_TIMELIMIT = "Čas vypršel a jak se zdá, stále jste nevydělal těch 800'000$, které jsem požadoval. Je to sice nešťastné, ale tohle je poslední den, kdy spolu pracujeme. Hodně štěstí příště.",
    ON_MY_OWN_GAMEOVER = "Investor vás nahradil s někým jiným, protože jste během 4 let nevydělali 800'000$.",
    THE_JOURNEY_DIALOGUE_1 = "Dobře, teď poslouchejte. Věřím, že tu hru stihnete dokončit do tří let. Na pozadí budu dělat pár věcí, abych vaši hru učinil trochu populárnější a prodávala se tak lépe. Takže se neflákejte a neutraťte všechno, co jsem vám dal!",
    THE_JOURNEY_DIALOGUE_QA = "Dobře, vaše hra je téměř hotova. Teď je čas ji trochu doladit. Což mi připomíná, že by jste měl najmout nějakou firmu pro zajištění kvality - zkráceně QA -  aby otestovala vaši hru. Určitě najdou plno chyb, které jste během vývoje přehlédl.",
    THE_JOURNEY_DIALOGUE_QA_2 = "Alternativně by jste mohl hru otestovat sám, ale to zabere mnohem více času, pokud nemáte plno zaměstnanců - což momentálně nemáte. Takže tohle je moje rada - najměte nějakou QA, nechte je najít chyby a pak je opravte.",
    THE_JOURNEY_DIALOGUE_2 = "Tak už máte hru! Teď ji prostě vydejte tak, jak je. Dělal jsem pro ni marketing, ale ne příliš, takže sice neočekávejte mnoho prodaných kusů, ale neočekávejte také nic!",
    THE_JOURNEY_DIALOGUE_3 = "Skvěle. Teď si jen sedněte, relaxujte a dívejte se, jak přichází peníze a recenze.",
    THE_JOURNEY_DIALOGUE_4 = "Hele, někdo už ohodnotil vaši hru! Měl byste se na jejich jecenzi podívat, abyste zjistil, co na ní bylo dobré a co špatné.",
    THE_JOURNEY_DIALOGUE_5_LOW_RATING = "No, mohlo to sice dopadnout lépe, ale nebuďte na sebe tvrdý - je to přece jenom vaše první hra.",
    THE_JOURNEY_DIALOGUE_5_MEDIUM_RATING = "To vůbec není špatné. Lepší skóre, než jsem očekával, abych byl upřímný.",
    THE_JOURNEY_DIALOGUE_5_HIGH_RATING = "No tohle, lidem se vaše hra líbí! Tohle jsem zcela upřímně nečekal!",
    THE_JOURNEY_DIALOGUE_5_2 = "Tak či tak, čtení recenzí je důležité, protože vám poskytnou náhled do toho, co v různých žánrech funguje a nefunguje. Takže nebuďte líný a čtěte je, pokud budete mít čas!",
    THE_JOURNEY_TIMELIMIT = "Čas vypršel. Bylo to 36 měsíců a já stále nevidím vaši hru vydanou. Je mi líto, ale tohle je konec.",
    THE_JOURNEY_GAMEOVER = "Investor vás nahradil s někým jiným, protože vaše první hra byla příliš dlouho ve vývoji.",
    VROOM_VROOM_DIALOGUE_1_1 = "Jsem si jist, že jste celý dychtivý skočit do vývoje her, ale vaše nadšení musím trochu zchladit. Budete muset totiž nejdřív vytvořit herní engine. Nemusí být nějak úžasný nebo následovat průmyslové standarty, měl by být prostě něco, s čím bude možné vyvinout hru.",
    VROOM_VROOM_DIALOGUE_1_2 = "Nemyslete si, že váš pokrok nebudu sledovat. Očekávám, že do šesti měsíců bude engine hotov.",
    VROOM_VROOM_TIMELIMIT = "Tik tak, čas vypršel! Promiň příteli, ale zdá se, že jsem se ve vás mýlil. Dnes ukončuji naše obchodní partnerství.",
    VROOM_VROOM_GAMEOVER = "Investor vás nahradil s někým jiným, protože vám trvalo příliš dlouho vyvinout engine.",
    VROOM_VROOM_DIALOGUE_2 = "Už jste hotov? Skvělé, ale teď přijde čas na něco obtížnějšího - vývoj vaší vlastní hry! Abych to ujasnil, budete mít čas tři roky. Proto začněte s něčím malým, jinak to nestihnete.",
    TUTORIAL_CONSTRUCTION_1_1 = "Vítejte! Tento tutoriál vás naučí základy konstrukce a zaplňování prázného kancelářského prostoru.",
    TUTORIAL_CONSTRUCTION_1_2 = "Kancelář bez pracovních míst je jen prázným, neužitečným prostorem. Pojďme to změnit!",
    TUTORIAL_CONSTRUCTION_2_1 = "Nejdříve položte několik pracovních míst, protože přece nechceme, aby světová špička ve vývoji softwaru seděla na zemi s notesy a něco si jen čmárala.",
    TUTORIAL_CONSTRUCTION_3_1 = "Možná si myslíte, že pracovní místa stačí, ale mýlíte se. Herní vývojáři jsou lidé jako kdokoliv jiný, a tak mají i své potřeby. Pojďme se o to proto postarat.",
    TUTORIAL_CONSTRUCTION_4_1 = "Skvěle! Teď už zbývá jen jedna věc - výdejník vody.",
    TUTORIAL_CONSTRUCTION_5_1 = "Teď je vaše kancelář připravena.",
    TUTORIAL_CONSTRUCTION_5_2 = "Můžete také vybudovat kuchyně a objekty spojené s komfortem, díky kterým se bude vaším zaměstnancům motivace vyčerpávat pomaleji, díky čemuž budou moct pracovat bez dovolených déle.",
    TUTORIAL_CONSTRUCTION_5_3 = "Některé kanceláře mohou být rozšířeny, což se většinou hodí. Je to mnohem levnější než koupit novou budovu.",
    TUTORIAL_CONSTRUCTION_6_1 = "Rozšiřování je jedna věc, ale nové kanceláře věc druhá. Nová budova vás vyjde mnohem dráž než rozšíření nějaké, kterou již vlastníte, ale také získáte spoustu nového volného místa.",
    TUTORIAL_CONSTRUCTION_7_1 = "A to je vše, co jsem vám chtěl ke konstrukci ukázat. Zbytek je již na vás. Ještě budete muset všechny objekty pokládat co nejefektivněji, aby bylo možné do dostupného prostoru položit co nejvíce pracovních míst.",
    TUTORIAL_CONSTRUCTION_7_2 = "Mimochodem, budovy mohou mít více než jedno patro. Vybudování nového poschodí je obvykle mnohem levnější než nákup nové kancelářské budovy, přičemž získáte stejné množství místa!",
    TUTORIAL_EMPLOYEES_1_1 = "Teď, když už máte decentní kancelář, je načase najmout pár zaměstnanců.",
    TUTORIAL_EMPLOYEES_1_2 = "Budete muset najmout osoby z různých profesí, protože herní vývojáři nejsou nějací zázrační všeumělové - každý má nějakou specializaci, ve které je nejlepší.",
    TUTORIAL_EMPLOYEES_1_3 = "Teď najměte jednoho zaměstnance od každé specializace.",
    TUTORIAL_EMPLOYEES_2_1 = "Jenom fakt, že jste někomu poslali pracovní nabídku, neznamená, že ji daná osoba přijme. Roli v tom hraje vaše finanční situace a reputace.",
    TUTORIAL_EMPLOYEES_3_1 = "Skvěle. Více lidí znamená, že práce na projektech bude probíhat rychleji, protože si ji mezi sebou zaměstnanci rozdělí.",
    TUTORIAL_EMPLOYEES_3_2 = "Vždycky myslete během najímání nových lidí na svou finanční situaci. Zaměstnanci totiž musí být placeni, a pokud si to nemůžete dovolit, začnou vás opouštět.",
    TUTORIAL_EMPLOYEES_3_3 = "Nezapomeňte, že zaměstnanci jsou obyčejní lidé jako vy! Často mají své vlastní zájmy, které jim poskytují znalosti v určitých oblastech. Toho může být využito k vytváření lepších her.",
    TUTORIAL_EMPLOYEES_3_4 = "Kromě toho má každý z nich atributy a dovednosti. Každá role - kromě vás - má svou hlavní dovednost, kterou lze zlepšovat mnohem rychleji než ostatní.",
    TUTORIAL_EMPLOYEES_3_5 = "Klikněte na jakéhokoliv zaměstnance a otevřete menu 'Informace o zaměstnanci'!",
    TUTORIAL_PROJECTS_1_1 = "A teď se konečně dostáváme k samému jádru tohoto průmyslu - vývoj her!",
    TUTORIAL_PROJECTS_1_2 = "Nejdčív budete muset sehnat herní engine. Můžete si buď vyvinout svůj, nebo si k nějakému existujícímu koupit licenci.",
    TUTORIAL_PROJECTS_2_1 = "S enginem už můžet začít pracovat na hře.",
    TUTORIAL_PROJECTS_2_2 = "Je tu mnoho proměnných, které uvlivňují úspěch vaší hry, ale vybrání každé možné funkce pod sluncem mezi ně nepatří.",
    TUTORIAL_PROJECTS_2_3 = "Velké množství funkcí je nutné jen pro hru, která bude prodávána za vysokou cenu.",
    TUTORIAL_PROJECTS_3_1 = "Vývoj her je rozdělen na tři fáze: koncept & design, vývoj a ladění",
    TUTORIAL_PROJECTS_3_2 = "První dvě fáze jsou povinné, ale ladění je povinné.",
    TUTORIAL_PROJECTS_3_3 = "Ve většině případů je ale přeskočení poslední vývojové fáze pro vaši hru podepsáním ortelu smrti. Nedoladěná hratelnost a obrovské množstvá chyb prodejům rozhodně neprospějí.",
    TUTORIAL_PROJECTS_4_1 = "Teď, když je vaše hra v ladící fázi, možná se budete chtít buď najmout na firmu pro zajištění kvality (neboli QA), nebo ji otestovat sám.",
    TUTORIAL_PROJECTS_4_2 = "QA bude hru testovat, zatímco vy na ní budete stále pracovat a problémy opravovat.",
    TUTORIAL_PROJECTS_4_3 = "Testování na vlastní pěst bude vyžadovat zaměstnance, což znamená přesměrování námahy od práce na hře. To znamená ztracený čas, který mohl být využit spíše pro její zlepšování.",
    TUTORIAL_PROJECTS_5_1 = "Hra je dokončena. V tomto bodě ji můžete buď vydat tak, jak je, nebo s laděním pokračovat dál.",
    TUTORIAL_PROJECTS_6_1 = "Hra je vydána a vaše práce je zde hotova.",
    TUTORIAL_PROJECTS_6_2 = "Jak zjistit, zda je hra dobrá nebo ne? Čtením recenzí, samozřejmě! Postupem času se nahromadí a pokud si je přečtete, získáte celkem dobrou představu o tom, co zafungovalo a co ne.",
    TUTORIAL_PROJECTS_7_1 = "Jediná recenze vám ale neřekne, co všechno vaše hra udělala správně a co ne. Více rencenzí vám dá větší obrázek, díky kterému budete schopni vytvářet ještě lepší hry.",
    TUTORIAL_PROJECTS_7_2 = "Dobrá kombinace tématu s žánrem navýší vaše prodeje vaší hry a mírně zlepší její skóre v recenzích, ale ani zdaleka ne natolik, aby ze špatné hry udělala dobrou. Nejdůležitější částí herního vývoje je mít zkušené vývojáře.",
    TUTORIAL_PROJECTS_7_3 = "Tohle je vše, co jsem vám chtěl vysvětlit. Teď, když už základy znáte, je načase, abyste se pustili do pořádné výzvy.",
